  componentDidUpdate(previousProps) {
    let updateState = false;
    let {
      selectedRecord,
      selectedRecordfilter,
      userRoleControlRights,
      controlMap,
      filterData,
      nfilterProjectType,
      filterProjectType
    } = this.state;

    if (this.props.Login.selectedRecord !== previousProps.Login.selectedRecord) {
      selectedRecord = this.props.Login.selectedRecord;
      updateState = true;
      this.setState({
        selectedRecord,
        selectedRecordfilter
      });
    }

    if (this.props.Login.userInfo.nformcode !== previousProps.Login.userInfo.nformcode) {
      userRoleControlRights = [];

      if (this.props.Login.userRoleControlRights) {
        this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode] && Object.values(this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode]).map(item => userRoleControlRights.push(item.ncontrolcode));
      }

      controlMap = getControlMap(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode);
      this.setState({
        controlMap,
        userRoleControlRights
      }); //   updateState = true;
    }

    let nprojecttypecode = this.state.nfilterProjectType || {};

    if (this.props.Login.masterData !== previousProps.Login.masterData) {
      nfilterProjectType = this.state.nfilterProjectType || {};
      const filterProjectType = constructOptionList(this.props.Login.masterData.filterProjectType || [], "nprojecttypecode", "sprojecttypename", undefined, undefined, undefined);
      let ProjectMasterList = filterProjectType.get("OptionList");
      nprojecttypecode = ProjectMasterList[0];
      selectedRecordfilter = {
        nprojecttypecodevalue: nprojecttypecode
      };
      selectedRecord = {
        nprojecttypecodevalue: nprojecttypecode
      }; // nfilterProjectType = this.props.Login.masterData.nfilterProjectType;

      this.setState({
        ProjectMasterList,
        selectedRecordfilter,
        selectedRecord
      });

      if (this.props.Login.masterData.SelectedProjectType && this.props.Login.masterData.SelectedProjectType !== previousProps.Login.masterData.SelectedProjectType) {
        nfilterProjectType = {
          label: this.props.Login.masterData.SelectedProjectType.sprojecttypename,
          value: this.props.Login.masterData.SelectedProjectType.nprojecttypecode,
          item: this.props.Login.masterData.SelectedProjectType
        };
      } //  selectedRecordfilter = {nprojecttypecodevalue:nfilterProjectType}


      filterData = this.generateBreadCrumData();
      updateState = true;
      this.setState({
        filterData,
        selectedRecordfilter
      });
    }

    if (this.props.Login.masterData.SelectedProjectType !== previousProps.Login.masterData.SelectedProjectType) {
      let nprojecttypecodevalue = this.props.Login.masterData.SelectedProjectType ? {
        label: this.props.Login.masterData.SelectedProjectType.sprojecttypename,
        value: this.props.Login.masterData.SelectedProjectType.nprojecttypecode,
        item: this.props.Login.masterData.SelectedProjectType
      } : ""; //  nprojecttypecode=ProjectMasterList[ProjectMasterList.length-1];

      selectedRecordfilter = {
        nprojecttypecodevalue
      };
      selectedRecord = {
        nprojecttypecodevalue: nprojecttypecode
      }; // nfilterProjectType = this.props.Login.masterData.nfilterProjectType;

      this.setState({
        selectedRecordfilter,
        selectedRecord
      });
    } // if (this.props.Login.masterData.nfilterProjectType !== previousProps.Login.masterData.nfilterProjectType) {
    // }

  }

  generateBreadCrumData() {
    const breadCrumbData = [];

    if (this.props.Login.masterData && this.props.Login.masterData.SelectedProjectType) {
      breadCrumbData.push({
        label: "IDS_PROJECTTYPE",
        value: this.props.Login.masterData.SelectedProjectType ? this.props.Login.masterData.SelectedProjectType.sprojecttypename : "NA"
      });
    } else if (this.props.Login.masterData && this.props.Login.masterData.SelectedProjectMaster) {
      /*    breadCrumbData.push({
            label: "IDS_PROJECTTYPE",
            value: this.state.selectedRecord.nprojecttypecode
              ? this.state.selectedRecord.nprojecttypecode.label
              : "NA",
          }); */
      breadCrumbData.push({
        label: "IDS_PROJECTTYPE",
        value: this.props.Login.masterData.SelectedProjectMaster ? this.props.Login.masterData.SelectedProjectMaster.sprojecttypename : "NA"
      });
    } else {
      /*       breadCrumbData.push({
             label: "IDS_PROJECTTYPE",
             value:  this.state.selectedRecord.nprojecttypecodevalue
               ? this.state.selectedRecord.nprojecttypecodevalue.label
               : "NA",
           }); */
      breadCrumbData.push({
        label: "IDS_PROJECTTYPE",
        value: this.props.Login.masterData.nfilterProjectType ? this.props.Login.masterData.nfilterProjectType.label : "NA"
      });
    }

    return breadCrumbData;
  }

}

export default connect(mapStateToProps, {
  callService,
  crudMaster,
  filterColumnData,
  updateStore,
  validateEsignCredential,
  changeProjectTypeFilter,
  viewAttachment,
  addProjectMaster,
  getProjectMaster,
  getProjectmasterAddMemberService,
  addProjectMasterFile,
  getuserComboServices
})(injectIntl(ProjectMaster));

