export function getTest(objComponent, LoginProps, nneedsubsample, specBasedComponent) {
  return function (dispatch) {
    if (Object.keys(objComponent).length > 0) {
      dispatch(initRequest(true));
      rsapi.post("/registration/getTestfromDB", {
        nspecsampletypecode: objComponent.nspecsampletypecode,
        slno: objComponent.slno,
        nneedsubsample: nneedsubsample,
        nallottedspeccode: objComponent.nallottedspeccode,
        specBasedComponent: specBasedComponent
      }).then(response => {
        let TestData = response.data;
        let Test = LoginProps.Test || [];
        let componentTest = Test[objComponent.slno] ? Test[objComponent.slno] : [];
        const TestCombined = filterRecordBasedOnTwoArrays(TestData, componentTest, "ntestgrouptestcode"); // const lstComponentMap = constructOptionList(TestCombined|| [], "ntestgrouptestcode",
        //"stestsynonym", undefined, undefined, true);

        dispatch({
          type: DEFAULT_RETURN,
          payload: {
            TestCombined,
            loadTest: true,
            openChildModal: false,
            ChildscreenName: "Test",
            screenName: "Test",
            operation: "create",
            childoperation: "create",
            parentPopUpSize: "lg",
            loading: false
          }
        });
      }).catch(error => {
        toast.error(error.message);
        dispatch({
          type: DEFAULT_RETURN,
          payload: {
            loading: false
          }
        });
      });
    } else {
      toast.warn(intl.formatMessage({
        id: "IDS_SELECTCOMPONENTTOADDTEST"
      }));
    }
  };
}
export function addsubSample(masterData, userinfo, columnList, selectComponent, childColumnList, SubSamplecomboComponents, SubSamplewithoutCombocomponent, specBasedComponent, Map, Component, isServiceRequired, SubSamplecomboData) {
  if (isServiceRequired) {
    return function (dispatch) {
      dispatch(initRequest(true));
      const urlArray = [];
      const timeZoneService = rsapi.post("timezone/getTimeZone");
      urlArray[0] = timeZoneService;
      const actualService = rsapi.post("dynamicpreregdesign/getComboValues", {
        parentcolumnlist: columnList,
        childcolumnlist: childColumnList,
        userinfo
      });
      urlArray[1] = actualService;

      if (specBasedComponent) {
        const ComponentTestBySpec = rsapi.post("/registration/getComponentBySpec", { ...Map,
          specBasedComponent: specBasedComponent
        });
        urlArray[2] = ComponentTestBySpec;
      } else {
        Component = Component ? Component : [];
        const TestGet = rsapi.post("/registration/getTestfromDB", {
          nallottedspeccode: Map["nallottedspeccode"],
          slno: Component ? Object.keys(Component).length + 1 : 1,
          //nneedsubsample: nneedsubsample,
          // nallottedspeccode: objComponent.nallottedspeccode,
          specBasedComponent: specBasedComponent
        });
        urlArray[2] = TestGet;
      }

      Axios.all(urlArray).then(response => {
        const timeZoneMap = constructOptionList(response[0].data || [], "ntimezonecode", "stimezoneid", undefined, undefined, true);
        const timeZoneList = timeZoneMap.get("OptionList");
        const defaultTimeZone = {
          label: userinfo.stimezoneid,
          value: userinfo.ntimezonecode
        };
        const newcomboData = parentChildComboLoad(columnList, response[1].data, selectComponent, childColumnList, SubSamplewithoutCombocomponent);
        let TestCombined = [];
        let lstComponent = [];

        if (specBasedComponent) {
          const lstComponentMap = constructOptionList(response[2].data || [], "ncomponentcode", "scomponentname", undefined, undefined, true);
          lstComponent = lstComponentMap.get("OptionList");
        } else {
          TestCombined = response[2].data;
        }

        const comboData1 = newcomboData.comboData;
        const selectedRecord1 = newcomboData.selectedRecord;
        SubSamplewithoutCombocomponent.map(componentrow => {
          if (componentrow.inputtype === "date") {
            if (componentrow.mandatory) {
              selectedRecord1[componentrow.label] = componentrow.loadcurrentdate ? new Date() : "";
            } // if (componentrow.timezone) {
            //     sampleRegistration["jsondata"][`tz${componentrow.label}`] = selectedRecord[`tz${componentrow.label}`] ?
            //         { value: selectedRecord[`tz${componentrow.label}`].value, label: selectedRecord[`tz${componentrow.label}`].label } :
            //         defaulttimezone ? defaulttimezone : -1
            // }

          }
        });
        dispatch({
          type: DEFAULT_RETURN,
          payload: {
            ChildscreenName: "SubSample",
            screenName: "SubSample",
            showSaveContinue: true,
            openChildModal: false,
            childoperation: "create",
            parentPopUpSize: "lg",
            loading: false,
            lstComponent,
            openPortal: true,
            openModal: false,
            operation: "create",
            loadSubSample: true,
            selectComponent: selectedRecord1,
            SubSamplecomboData: comboData1,
            SubSamplecomboComponents,
            SubSamplewithoutCombocomponent,
            TestCombined,
            parentSubSampleColumnList: columnList,
            timeZoneList,
            defaultTimeZone,
            masterData
          }
        });
      }).catch(error => {
        toast.error(error.message);
        dispatch({
          type: DEFAULT_RETURN,
          payload: {
            loading: false
          }
        });
      });
    };
  } else {
    const newcomboData = parentChildComboLoad(columnList, SubSamplecomboData, selectComponent, childColumnList, SubSamplewithoutCombocomponent);
    return dispatch => {
      if (specBasedComponent === 4) {
        rsapi.post("/registration/getTestfromDB", {
          nallottedspeccode: Map["nallottedspeccode"],
          slno: Component ? Object.keys(Component).length + 1 : 1,
          //nneedsubsample: nneedsubsample,
          // nallottedspeccode: objComponent.nallottedspeccode,
          specBasedComponent: specBasedComponent
        }).then(response => {
          const TestCombined = response.data;
          dispatch({
            type: DEFAULT_RETURN,
            payload: {
              ChildscreenName: "SubSample",
              showSaveContinue: true,
              openChildModal: false,
              childoperation: "create",
              parentPopUpSize: "lg",
              loading: false,
              lstComponent: [],
              openPortal: true,
              openModal: false,
              operation: "create",
              loadSubSample: true,
              selectComponent: newcomboData.selectedRecord,
              TestCombined: TestCombined
            }
          });
        });
      }
    };
  }
}
