                            // currentTime,
                            operation,
                            //  screenName: popUpLabel,
                            // PopUpLabel: popUpLabel,
                            timeZoneList,
                            defaultTimeZone,
                            selectedRecord,
                            openPortal: true,
                            ncontrolCode: inputParam.editRegParam.ncontrolCode,
                            loadPreregister: true,
