                        dfrom: obj.fromDate,
                        dto: obj.toDate,
                        nflag: 2,
                        ntype: 1,
                        isAddPopup:false,
                        userinfo: this.props.Login.userInfo,
                        action,
                        ndesigntemplatemappingcode: (this.props.Login.masterData.ndesigntemplatemappingcode && this.props.Login.masterData.ndesigntemplatemappingcode) || -1,
                    },
                    userinfo: this.props.Login.userInfo,
                    methodUrl: "Release",
                    classUrl: "release",
                    operation:this.props.Login.operation
                }
                if (nflag === true) {
                    this.props.generateReleasedReport(inputParam.inputData, this.props.Login.masterData, "isPreview")
                }
                else {
                    const masterData = this.props.Login.masterData;
                    const saveType = nflag;
                    if (showEsign(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode,this.props.Login.ncontrolCode)) {

                        const updateInfo = {
                            typeName: DEFAULT_RETURN,
                            data: {
                                loadEsign: true,
