                //url = "resultentrybysample/getTestbasedParameter";
                url = "registration/getregistrationparameter";
                break;
            case "IDS_TESTCOMMENTS":
                url = "comments/getTestComment";
                break;
            default:
                url = "registration/getregistrationparameter";
                // url = "resultentrybysample/getTestbasedParameter";
                break;
        }
        return url;
