                                },
                                selectedRecord: {
                                    ncomponentcode: sampleTypeMap.get("DefaultValue"),
                                    ntestcategorycode: testCategory.get("DefaultValue") ? testCategory.get("DefaultValue") : testCategory.get("OptionList")[0]
                                },
