import * as React from 'react';
import { injectIntl } from 'react-intl'
import { Row, Col, Form } from 'react-bootstrap';
import '@progress/kendo-react-animation'
import FormInput from '../../components/form-input/form-input.component';
import TreeViewEditable from '../../components/form-tree-editable/form-tree-editable.component';
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';

const AddTreeview = (props) => {
  return (
    <Row>

      <Col md={12}>
        <FormSelectSearch
          name={"nstoragecategorycode"}
          formLabel={props.intl.formatMessage({ id: "IDS_STORAGECATEGORY" })}
          placeholder={props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
          options={props.storageCategoryList}
          value={props.selectedRecord["nstoragecategorycode"]}
          isMandatory={true}
          isClearable={false}
          isMulti={false}
          isSearchable={true}
          isDisabled={props.isOnlyDraft}
          closeMenuOnSelect={true}
          onChange={(event) => props.onComboChange(event, 'nstoragecategorycode',1)}
        />
      </Col>
      <Col md={12}>
        <FormInput
          label={props.intl.formatMessage({ id: "IDS_SAMPLESTORAGELOCATIONNAME" })}
          name={"ssamplestoragelocationname"}
          type="text"
          onChange={(event) => props.onInputChange(event)}
          placeholder={props.intl.formatMessage({ id: "IDS_SAMPLESTORAGELOCATIONNAME" })}
          value={props.selectedRecord ? props.selectedRecord["ssamplestoragelocationname"] : ""}
          isMandatory={true}
          required={true}
          maxLength={"30"}
          isDisabled={props.isOnlyDraft}
        />
      </Col>
      <Col md={12}>
        <TreeViewEditable
          id="samplestoragelocation"
          name="samplestoragelocation"
          // label="Sample Storage Location"
          placeholder="Enter samplestoragelocation"
          data={props.treeData}
          expandIcons={true}
          item={props.itemRender}
          onExpandChange={props.onExpandChange}
          onItemClick={props.onItemClick}
        />
      </Col>
    </Row>)
}
export default injectIntl(AddTreeview);
