  componentDidUpdate(previousProps) {
    if (this.props.Login.masterData !== previousProps.Login.masterData) {
      if (this.props.Login.userInfo.nformcode !== previousProps.Login.userInfo.nformcode) {
        const userRoleControlRights = [];

        if (this.props.Login.userRoleControlRights) {
          this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode] && Object.values(this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode]).map(item => userRoleControlRights.push(item.ncontrolcode));
        }

        const controlMap = getControlMap(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode);
        const multilingualmastersMap = constructOptionList(this.props.Login.masterData.multilingualmasters || [], "nmultilingualmasterscode", "sdisplayname", "nsorter", 'ascending', undefined, true);
        const multilingualmasters = multilingualmastersMap.get("OptionList");
        this.setState({
          userRoleControlRights,
          controlMap,
          multilingualmasters: multilingualmasters,
          data: this.props.Login.masterData,
          dataResult: process(this.props.Login.masterData.listofItem ? this.props.Login.masterData.listofItem : [], this.state.dataState)
        });
      } else {
        let {
          dataState
        } = this.state;

        if (this.props.Login.dataState === undefined) {
          dataState = {
            skip: 0,
            take: this.props.Login.settings ? parseInt(this.props.Login.settings[14]) : 5
          };
        }

        const FilterQueryMap = constructOptionList(this.props.Login.masterData.QueryBuilderViewsName || [], "nquerybuilderviewscode", "sdisplayname", undefined, undefined, true);
        const QueryBuilderViewsName = FilterQueryMap.get("OptionList");
        const formNameQueryMap = constructOptionList(this.props.Login.filterValue || [], "nformcode", "sformname", undefined, undefined, true);
        const MaterialConfig = formNameQueryMap.get("OptionList");
        this.setState({
          data: this.props.Login.masterData,
          QueryBuilderViewsName: QueryBuilderViewsName,
          MaterialConfig: MaterialConfig,
          dataResult: process(this.props.Login.masterData.listofItem ? this.props.Login.masterData.listofItem : [], dataState),
          dataState
        });
      }
    } else if (this.props.Login.selectedRecord !== previousProps.Login.selectedRecord) {
      let selectedRecord;

      if (this.props.Login.masterData.headername === "Query Builder Views Columns") {
        selectedRecord = { ...this.props.Login.selectedRecord,
          sdefaultname: this.props.Login.selectedRecord.sdefaultname
        };
      } else {
        selectedRecord = { ...this.props.Login.selectedRecord
        };
      }

      this.setState({
        selectedRecord
      });
    }
  }

  handleClose() {
    let loadEsign = this.props.Login.loadEsign;
    let openModal = this.props.Login.openModal;
    let selectedRecord = this.props.Login.selectedRecord;
    let selectedId = this.props.Login.selectedId;

    if (this.state.showSynonym) {
      this.setState({
        showSynonym: false
      });
      return null;
    }

    if (this.props.Login.loadEsign) {
      loadEsign = false;
      openModal = false;
    } else {
      openModal = false;
      selectedRecord = { ...this.props.Login.selectedRecord
      };
      selectedId = null;
    }

    const updateInfo = {
      typeName: DEFAULT_RETURN,
      data: {
        openModal,
        loadEsign,
        selectedRecord,
        selectedId
      }
    };
    this.props.updateStore(updateInfo);
  }

}

export default connect(mapStateToProps, {
  callService,
  crudMaster,
  showRegTypeAddScreen,
  fetchById,
  comboService,
  updateStore,
  validateEsignCredential
})(injectIntl(Languages));

