        QuotationData["nprojectmastercode"] = this.props.Login.masterData.SelectedQuotation.nprojectmastercode;


        return QuotationData;
    });
    inputData['QuotationTest'] = testArray;

    const inputParam = {
        classUrl: "quotation",
        methodUrl: "QuotationTest",
