                                    </Col>
                                </Row>
                                : this.props.Login.loadCustomSearchFilter ?
                                    this.props.Login.seletedFilterComponent &&
                                        this.props.Login.seletedFilterComponent.inputtype === 'frontendsearchfilter' ?
                                        <KendoDatatoolFilter
                                            filter={this.state.kendoFilter}
