                                                                                this.props.Login.masterData.registrationTemplate.jsondata :
                                                                                this.props.Login.masterData.schedulerTemplate ?
                                                                                    this.props.Login.masterData.schedulerTemplate.jsondata : {}}
                                                                            handleChange={this.props.handleChange}
