    insertRegistrationScheduler, testPackageTest, addMasterRecord,
    getAddMasterCombo, getDynamicMasterTempalte,
    getChildComboMaster, getChildValuesForAddMaster, viewExternalportalDetail
}
)(injectIntl(PreRegisterSlideOutModal));
