import React, { Component } from 'react';
import { Col, Row } from 'react-bootstrap';
import { injectIntl } from 'react-intl';
import FormInput from '../../components/form-input/form-input.component';
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';

class AddSubSample extends Component {
    render() {
        return (
            <Row>
                <Col md={12}>
                    <FormInput
                        label={this.props.intl.formatMessage({ id: "IDS_SUBSAMPLENAME" })}
                        name="ssubsamplename"
                        type="text"
                        maxLength="100"
                        isMandatory={true}
                        value={this.props.selectedSubSample?this.props.selectedSubSample["ssubsamplename"]:""}
                        onChange={(event) => this.props.onInputOnChange(event)}
                        placeholder={this.props.intl.formatMessage({ id: "IDS_MANUFACTURERSITE" })}
                        isDisabled={true}
                    />
             

                <Row>
                    <Col md={'6'}>
                        <FormInput
                            label={this.props.intl.formatMessage({ id: "IDS_TOTALQUANTITY" })}
                            name="stotalqty"
                            type="text"
                            maxLength="100"
                            isMandatory={true}
                            value={this.props.selectedSubSample?this.props.selectedSubSample["stotalqty"] :""}
                            onChange={(event) => this.props.onInputOnChange(event)}
                            placeholder={this.props.intl.formatMessage({ id: "IDS_TOTALQUANTITY" })}
                            isDisabled={true}
                        />
                    </Col>
                    <Col md={'6'}>

                        <FormSelectSearch
                            formLabel={this.props.intl.formatMessage({ id: "IDS_UNIT" })}
                            isSearchable={true}
                            name={"nunitcode"}
                            isDisabled={false}
                            placeholder={this.props.intl.formatMessage({ id: "IDS_UNIT" })}
                            isMandatory={true}
                            options={this.props.Unit||[]}
                            alphabeticalSort="true"
                            optionId="nunitcode"
                            optionValue="sunitname"
                            value={this.props.selectedSubSample ? this.props.selectedSubSample["nunitcode"] : ""}
                            defaultValue={this.props.selectedSubSample ? this.props.selectedSubSample["nunitcode"] : ""}
                            //  showOption={true}
                            closeMenuOnSelect={true}
                            onChange={(event) => this.props.onSubSampleComboChange(event, 'nunitcode')}>
                        </FormSelectSearch>
                    </Col>
                </Row>
                <FormInput
                            label={this.props.intl.formatMessage({ id: "IDS_COMMENTS" })}
                            name="scomments"
                            type="text"
                            maxLength="100"
                            isMandatory={true}
                            value={this.props.selectedSubSample ?this.props.selectedSubSample["scomments"]:""}
                            onChange={(event) => this.props.onInputOnChange(event)}
                            placeholder={this.props.intl.formatMessage({ id: "IDS_COMMENTS" })}
                            isDisabled={true}
                        />
                    </Col>

            </Row>
        );
    }
}

export default injectIntl(AddSubSample);
