                {
                    "label": "IDS_REGTYPE",
                    "value": selectedRegType.label,
                    "item": selectedRegType
                }
            ];
            breadCrumbSampleType = this.props.Login.realSampleType;
            breadCrumbRegType = this.props.Login.realRegtype;
            updateState = true;
        } if (this.props.Login.realRegtype !== previousProps.Login.realRegtype) {
            breadCrumbSampleType = this.props.Login.realSampleType || breadCrumbSampleType;
            breadCrumbRegType = this.props.Login.realRegtype;
