import React, { version } from 'react'
import { Row, Col, Card } from 'react-bootstrap';//, Nav, Tab
//import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
//import { faPlus } from '@fortawesome/free-solid-svg-icons';//,faPencilAlt, faTrash
import { injectIntl } from 'react-intl';//FormattedMessage,
//import { Grid, GridColumn } from '@progress/kendo-react-grid';
import { process } from '@progress/kendo-data-query';
import SlideOutModal from '../../../components/slide-out-modal/SlideOutModal';
//import Axios from 'axios';
//import rsapi from '../../rsapi';
import { DEFAULT_RETURN } from '../../../actions/LoginTypes';
import { constructOptionList, onDropAttachFileList ,deleteAttachmentDropZone, showEsign,create_UUID} from '../../../components/CommonScript';
//import DataGrid from '../../components/DataGrid';
import CustomTabs from '../../../components/custom-tabs/custom-tabs.component';
import { connect } from 'react-redux';
import TrainingParticipantsTab from './TrainingParticipantsTab';
import TrainingDocTab from './TrainingDocTab';
import AddTrainingDocFile from './AddTrainingDocFile';
import AddTraineeDocFile from './AddTraineeDocFile';
import Esign from '../../audittrail/Esign';
import { attachmentType, FORMULAFIELDTYPE, transactionStatus } from '../../../components/Enumeration';
import { Expression } from '@progress/kendo-react-data-tools';

const mapStateToProps = state => {
    return ({ Login: state.Login })
}
class TrainingUpdateChildTab extends React.Component {

    constructor(props) {
        super(props);
        const dataState = {
            skip: 0,
            take: this.props.settings? parseInt(this.props.settings[14]) : 5
        };
        const dataStateParticipants = {
            skip: 0,
            take:this.props.settings? parseInt(this.props.settings[14]) : 5
        };
        this.state = {
            isOpen: false, selectedRecord: {}, dataResult: [],
            activeTab: 'Trainingdoc-tab',
            dataState: dataState,
            dataStateParticipants: dataStateParticipants, trainingDocument: [], participants: []
        };
        this.trainingDocumentFieldList = ['ntrainingdoccode', 'nparticipantcode', 'ntrainingcode', 'sfilename', 'ntechniquecode', 'sfullname',
            'nstatus', 'ntransactionstatus'];
        this.trainingDocumentColumnList = [{ "idsName": "IDS_TRAININGDOCUMENTNAME", "dataField": "sfilename", "width": "200px", "mandatory": true, "mandatoryLabel":"IDS_ENTER", "controlType": "textbox" }];
        this.participantsColumnList = [{ "idsName": "IDS_PARTICIPANTSNAME", "dataField": "sfullname", "width": "200px", "mandatory": true, "mandatoryLabel":"IDS_ENTER", "controlType": "textbox" }];
        this.validationTrainingDocumentColumnList = [{ "idsName": "IDS_TRAININGDOCUMENTNAME", "dataField": "sfilename", "width": "200px", "mandatory": true, "mandatoryLabel":"IDS_SELECT", "controlType": "selectbox" }];
        this.validationParticipantsColumnList = [{ "idsName": "IDS_PARTICIPANTS", "dataField": "sfullname", "width": "200px", "mandatory": true, "mandatoryLabel":"IDS_SELECT", "controlType": "selectbox" }];
    }
    TrainingDocumentDataStateChange = (event) => {
        this.setState({
            dataState: event.dataState
        });
    }
    ParticipantsDataStateChange = (event) => {
        this.setState({
            dataStateParticipants: event.dataState
        });
    }
    render() {
        const mandatoryFields = [];
        //const matmandatoryFields = [];
        this.validationTrainingDocumentColumnList.forEach(item => item.mandatory === true ?
           mandatoryFields.push(item) : ""
         );
        // this.validationParticipantsColumnList.forEach(item => item.mandatory === true ?
        //     matmandatoryFields.push(item) : ""
        // );
        // if (this.props.Login.openModal) {
        //     this.mandatoryFields = this.findMandatoryFields(this.props.Login.screenName, this.state.selectedRecord, this.props.Login.operation)
        // }
        return (
            <>
                <Row className="no-gutters">
                    <Col md={12}>
                        <Card className="at-tabs">
                            <CustomTabs tabDetail={this.tabDetail()} onTabChange={this.onTabChange} />
                        </Card>
                    </Col>
                </Row>
                {this.props.Login.openModal &&
                    <SlideOutModal show={this.props.Login.openModal}
                        closeModal={this.closeModal}
                        operation={this.props.Login.operation}
                        inputParam={this.props.Login.inputParam}
                        screenName={this.props.Login.screenName}
                        onSaveClick={this.onSaveClick}
                        esign={this.props.Login.loadEsign}
                        validateEsign={this.validateEsign}
                        masterStatus={this.props.Login.masterStatus}
                        updateStore={this.props.updateStore}
                        selectedRecord={this.state.selectedRecord || {}}
                        mandatoryFields={mandatoryFields}
                        addComponent={this.props.Login.loadEsign ?
                            <Esign operation={this.props.Login.operation}
                                formatMessage={this.props.intl.formatMessage}
                                onInputOnChange={this.onInputOnChange}
                                inputParam={this.props.Login.inputParam}
                                selectedRecord={this.state.selectedRecord || {}}
                            />
                             : this.props.screenName === "IDS_TRAININGDOCUMENTS" ?
                            <AddTrainingDocFile
                                selectedRecord={this.state.selectedRecord || {}}
                                onDrop={this.onDropTrainingDoc}
                                onInputOnChange={this.onInputOnChange}
                                //onComboChange={this.onComboChange}
                                deleteAttachment={this.deleteAttachment}
                                formatMessage={this.props.intl.formatMessage}
                                //selectedmaHolder={this.props.Login.masterData.SelectedMAHolder || {}}
                                label={this.props.intl.formatMessage({ id: "IDS_FILENAME" })}
                                operation={this.props.Login.operation}
                                userLogged={this.props.Login.userLogged}
                                inputParam={this.props.Login.inputParam}
                                selectedId={this.props.Login.selectedId}
                            />
                            : this.props.screenName === "IDS_TRAINEEDOCUMENTS" ?
                            <AddTraineeDocFile
                                selectedRecord={this.state.selectedRecord || {}}
                                onDrop={this.onDropTrainieeDoc}
                                onInputOnChange={this.onInputOnChange}
                                //onComboChange={this.onComboChange}
                                deleteAttachment={this.deleteAttachment}
                                formatMessage={this.props.intl.formatMessage}
                                //selectedmaHolder={this.props.Login.masterData.SelectedMAHolder || {}}
                                label={this.props.intl.formatMessage({ id: "IDS_FILENAME" })}
                                operation={this.props.Login.operation}
                                userLogged={this.props.Login.userLogged}
                                inputParam={this.props.Login.inputParam}
                                selectedId={this.props.Login.selectedId}
                            />
                            : ""
                        }
                    />
                }
            </>

        )

    }


    tabDetail = () => {
        // const addSupCatId = this.props.controlMap.has("AddSupplierCategory") && this.props.controlMap.get("AddSupplierCategory").ncontrolcode

        // const deleteSupCatId = this.props.controlMap.has("DeleteSupplierCategory") && this.props.controlMap.get("DeleteSupplierCategory").ncontrolcode

        // const addMatCatId = this.props.controlMap.has("AddMaterialCategory") && this.props.controlMap.get("AddMaterialCategory").ncontrolcode

        // const deleteMatCatId = this.props.controlMap.has("DeleteMaterialCategory") && this.props.controlMap.get("DeleteMaterialCategory").ncontrolcode

        // const supcatAddParam = {
        //     screenName: "SupplierCategory", operation: "create", primaryKeyField: "nsuppliercatcode",
        //     masterData: this.props.masterData, userInfo: this.props.userInfo, ncontrolCode: addSupCatId
        // };

        // const supcatDeleteParam = { screenName: "SupplierCategory", methodUrl: "SupplierMatrix", operation: "delete", ncontrolCode: deleteSupCatId };

        // const matericatAddParam = {
        //     screenName: "MaterialCategory", operation: "create", primaryKeyField: "nmaterialcatcode",
        //     masterData: this.props.masterData, userInfo: this.props.userInfo, ncontrolCode: addMatCatId
        // };

        // const materialcatDeleteParam = { screenName: "MaterialCategory", methodUrl: "SupplierMatrix", operation: "delete", ncontrolCode: deleteMatCatId };

        const tabMap = new Map();
        tabMap.set("IDS_TRAININGDOCUMENTS", <TrainingDocTab
                                                screenName={"TrainingDocuments"}

                                                    //     inputParam={this.props.inputParam}
                                                    //     userInfo={this.props.userInfo}
                                                    //     screenName={"TrainingDocument"}  
                                                    //    // addId={addSupCatId}
                                                    //    //addParam={supcatAddParam}
                                                    //    //comboDataService={this.props.getSupplierCategoryComboDataService}
                                                    //     addTitleIDS={"IDS_SUPPLIERCATEGORY"}
                                                    //     addTitleDefaultMsg={'TrainingDocument'}
                                                      // primaryKeyField={"ntrainingdoccode"}
                                                    //     masterData={this.props.masterData}
                                                    //     primaryList={"TrainingDocument"}
                                                    //     dataResult={process(this.props.masterData["TrainingDocument"], this.state.dataState)}
                                                    //     // dataState={this.state.dataState}
                                                    //     // dataStateChange={this.supplierDataStateChange}
                                                    //     dataState={(this.props.screenName === undefined || this.props.screenName === ''|| this.props.screenName === "TrainingDocument") ? this.state.dataState : { skip: 0 }}
                                                    //     dataStateChange={(event) => this.setState({ dataState: event.data })}
                                                    //     columnList={this.trainingDocumentColumnList}
                                                    //     methodUrl={"TrainingDocument"}
                                                    //    // deleteRecord={this.deleteRecord}
                                                    //     //deleteParam={supcatDeleteParam}
                                                    // //selectedId={this.props.selectedId}
                                                    //openModal={this.openModal}
                                                    //addtrainingdocfile={this.addtrainingdocfile}
                                                    //selectedRecord={this.state.selectedRecord }
                                                    onInputOnChange={this.onInputOnChange}
                                                    controlMap={this.props.controlMap}
                                                    updateStore={this.props.updateStore}
                                                    userRoleControlRights={this.props.userRoleControlRights}
                                                    userInfo={this.props.userInfo}
                                                    inputParam={this.props.inputParam}
                                                    deleteRecord={this.deleteRecord}
                                                    TrainingDocument={this.props.masterData.TrainingDocument || []}
                                                    getAvailableData={this.props.getAvailableData}
                                                    //addTrainingDoc={this.props.addTrainingDoc}                                                   
                                                    //defaultRecord={this.defaultRecord}
                                                    settings = {this.props.settings}
                                                    />)
        tabMap.set("IDS_PARTICIPANTS", <TrainingParticipantsTab 
                                                          
                                                            userRoleControlRights={this.props.userRoleControlRights}
                                                            controlMap={this.props.controlMap}
                                                            inputParam={this.props.inputParam}
                                                            userInfo={this.props.userInfo}
                                                            screenName={" TrainieeDocuments"}   
                                                            updateStore={this.props.updateStore}
                                                            //addId={addMatCatId}
                                                            //addParam={matericatAddParam}
                                                            //comboDataService={this.props.getMaterialCategoryComboDataService}
                                                            //addTitleIDS={"IDS_PARTICIPANTSNAME"}
                                                            //addTitleDefaultMsg={'Participants'}
                                                            primaryKeyField={"nparticipantcode"}
                                                            masterData={this.props.masterData}
                                                            primaryList={"Participants"}
                                                            dataResult={process(this.props.Login.masterData["Participants", "TraineeDocuments"] || [], this.state.dataStateParticipants)}
                                                            dataState={this.state.dataStateParticipants}
                                                            // dataStateChange={this.materialDataStateChange}
                                                            dataState={(this.props.screenName === undefined || this.props.screenName === "Participants") ? this.state.dataStateParticipants : { skip: 0 }}
                                                            dataStateChange={(event) => this.setState({ dataStateParticipants: event.data })}
                                                            columnList={this.participantsColumnList}
                                                            methodUrl={"TrainingParticipants"}
                                                            fetchParticipantsRecordByID={this.fetchParticipantsRecordByID}
                                                            //participantsVersionAccordion={this.participantsVersionAccordion}
                                                            deleteRecord={this.deleteRecord}
                                                            //deleteParam={materialcatDeleteParam}
                                                        //selectedId={this.props.selectedId}
                                                        //TraineeDocuments={this.props.masterData.TraineeDocuments || []}
                                                        getAvailableData={this.props.getAvailableData}
                                                        addtraineedoc={this.addtraineedoc}
                                                        //settings = {this.props.settings}
                                                        
                                                        />)
        return tabMap;
    }


    onInputOnChange = (event,optional) => {
        const selectedRecord = this.state.selectedRecord || {};
       
           
                if (event.target.type === 'checkbox') {
                    selectedRecord[event.target.name] = event.target.checked === true ? optional[0] : optional[1];
                } else if (event.target.type === 'radio') {
                    selectedRecord[event.target.name] = optional;
                  //  selectedRecord["sfilename"]="";
                } else {
                    selectedRecord[event.target.name] = event.target.value;
                }
                this.setState({ selectedRecord });
             
                // const inputValue = event.target.value;
                // if (/^-?\d*?\.?\d*?$/.test(inputValue) || inputValue === "") {
                //     selectedRecord[event.target.name] = event.target.value;
                // }
                // this.setState({ selectedRecord });
           
                }

                addtraineedoc =(ncontrolcode) => {
        const updateInfo = {
            typeName: DEFAULT_RETURN,
            data: {
                openModal: true,
                operation: "create",
                selectedRecord: {},
                ncontrolcode: ncontrolcode,
                screenName : "IDS_TRAINEEDOCUMENTS" 

            }
        }
        this.props.updateStore(updateInfo);
    }
    closeModal = () => {
        let loadEsign = this.props.Login.loadEsign;
        let openModal = this.props.Login.openModal;
        let selectedRecord = this.props.Login.selectedRecord;
        if (this.props.Login.loadEsign) {
            if (this.props.Login.operation === "delete" ||
                 this.props.Login.operation === "certified" ||
                 this.props.Login.operation === "attendance" ||
                 this.props.Login.operation === "competent" || this.props.Login.operation === "complete") {
                loadEsign = false;
                openModal = false;
                selectedRecord = {};
            }
            else {
                loadEsign = false;
            }
        }
        else {
            openModal = false;
            selectedRecord = {};
        }

        const updateInfo = {
            typeName: DEFAULT_RETURN,
            data: { openModal, loadEsign, selectedRecord, selectedId: null }
        }
        this.props.updateStore(updateInfo);

    }
    onDropTrainingDoc = (attachedFiles, fieldName) => {

        let selectedRecord = this.state.selectedRecord || {};
        selectedRecord[fieldName] = onDropAttachFileList(selectedRecord[fieldName], attachedFiles)
        this.setState({ selectedRecord, actionType: "new" });
    }
    onDropTrainieeDoc = (attachedFiles, fieldName) => {

        let selectedRecord = this.state.selectedRecord || {};
        selectedRecord[fieldName] = onDropAttachFileList(selectedRecord[fieldName], attachedFiles)
        this.setState({ selectedRecord, actionType: "new" });
    }
    validateEsign = () => {
        const inputParam = {
            inputData: {
                "userinfo": {
                    ...this.props.Login.userInfo,
                    sreason: this.state.selectedRecord["esigncomments"]
                },
                password: this.state.selectedRecord["esignpassword"]
            },
