    if (this.props.Login.userInfo && this.props.Login.userInfo.nformcode !== previousProps.Login.userInfo.nformcode) {
      const nformCode = this.props.Login.userInfo.nformcode;

      if (this.props.Login.userRoleControlRights) {
        this.props.Login.userRoleControlRights[nformCode] && Object.values(this.props.Login.userRoleControlRights[nformCode]).map(item => userRoleControlRights.push(item.ncontrolcode));
      }

      controlMap = getControlMap(this.props.Login.userRoleControlRights, nformCode);
      filterData = { ...tempFilterData
      };
      this.generateBreadCrumData(filterData);
    }

    if (this.props.Login.filterData !== previousProps.Login.filterData) {
      filterData = { ...this.props.Login.filterData
      };
      this.generateBreadCrumData(filterData);
      isStateChanged = true;
    } // else if (filterData !== previousState.filterData) {
    //     filterData = { ...tempFilterData };
    //     this.generateBreadCrumData(filterData);
    // }


    let historyDataState = this.state.historyDataState;

    if (this.props.Login.historyDataState && this.props.Login.historyDataState !== previousProps.Login.historyDataState) {
      historyDataState = this.props.Login.historyDataState;
      isStateChanged = true;
    }

    if (isStateChanged) {
      this.setState({
        selectedRecord,
        tempFilterData,
        filterData,
        userRoleControlRights,
        controlMap,
        sampleType,
        productCategory,
        product,
        treeVersionTemplate,
        historyDataState
      });
    }
  }

}

const mapStateToProps = state => {
  return {
    Login: state.Login
  };
};

export default connect(mapStateToProps, {
  callService,
  crudMaster,
  filterTestGroup,
  createTree,
  editTree,
  sampleTypeOnChange,
  getTestGroupDetails,
  validateEsignCredential,
  updateStore,
  addSpecification,
  getSpecification,
  addComponent,
  changeTestCategory,
  addTestGroupTest,
  editTestGroupTest,
  getTestGroupParameter,
  editTestGroupParameter,
  addTestFile,
  editSpecFile,
  getSpecificationDetails,
  addTestGroupCodedResult,
  getComponentBySpecId,
  filterColumnData,
  viewAttachment,
  viewTestGroupCheckList,
  getTestGroupComponentDetails,
  filterTransactionList,
  reportSpecification,
  retireSpecification
})(injectIntl(TestGroup));

