                                                           // inputParam =({testGetParam,MJSelectedTest:this.props.Login.masterData.MJSelectedTest,userInfo:this.props.Login.userInfo})
                                                            btnClasses="btn-circle btn_grey ml-2"
                                                            dynamicButton={(value) => this.props.getAcceptTestTestWise(value,TestMyJobActionId,testGetParam,this.props.Login.masterData.MJSelectedTest,this.props.Login.userInfo)}
                                                            //onClick={() => this.props.getAcceptTestTestWise({testGetParam, MJSelectedTest: this.props.Login.masterData.MJSelectedTest,userInfo: this.props.Login.userInfo, ncontrolcode:TestAcceptActionId})}

                                                            textKey="scontrolname"
