            dataState = this.state.instrumentCatDataState;
        }
        const inputParam = {
            inputData: {
                ncontrolCode: deleteParam.ncontrolCode,
                [methodUrl.toLowerCase()]: selected,
                userinfo: this.props.Login.userInfo
            },
            classUrl: "material",
            operation: deleteParam.operation,
            ncontrolCode: deleteParam.ncontrolCode,
