            isneedcombomulti = false;
            selectedRecord = {};
        }
        const updateInfo = {
            typeName: DEFAULT_RETURN,
            data: { openModal, showMaterialSection, loadEsign, selectedRecord, selectedId: null }
        }
        this.props.updateStore(updateInfo);
