import rsapi from '../rsapi';
import {
    DEFAULT_RETURN
} from './LoginTypes';
import {
    toast
} from 'react-toastify';
import {
    initRequest
} from './LoginAction';
import Axios from 'axios';
import {
    intl
} from "../components/App";
import { transactionStatus } from '../components/Enumeration';
import { constructOptionList,rearrangeDateFormat } from '../components/CommonScript';

export function getBatchCreationDetails(inputData) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post("batchcreation/getBatchmaster", inputData)
            .then(response => {
                dispatch({
                    type: DEFAULT_RETURN,
                    payload: {
                        masterData: {
                            ...inputData.masterData,
                            ...response.data,
                            // fromDate: inputData.fromdate,
                            //  toDate: inputData.todate
                        },
                        loading: false
                    }
                })
            })
            .catch(error => {
                dispatch({
                    type: DEFAULT_RETURN,
                    payload: {
                        loading: false
                    }
                })
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
            })
    }
}


