                      formData.append("uniquefilename" + index, uniquefilename);
                      formData.append("ntransactiontestcode", resultData.ntransactiontestcode);
                      i++;
                    }

                    break;

                  default:
                    break;
                }

                if (Object.values(results).length > 0) {
                  resultParameters.push(results);
                }
              }

              return null;
