      SampleType = constructOptionList(this.props.Login.masterData.SampleType || [], "nsampletypecode", "ssampletypename", 'nsampletypecode', 'ascending', 'nsampletypecode', false);
      RegistrationType = constructOptionList(this.props.Login.masterData.RegistrationType || [], "nregtypecode", "sregtypename", "nsorter", 'ascending', 'nregtypecode', false);
      RegistrationSubType = constructOptionList(this.props.Login.masterData.RegistrationSubType || [], "nregsubtypecode", "sregsubtypename", "nsorter", 'ascending', 'nregsubtypecode', false);
      REFilterStatus = constructOptionList(this.props.Login.masterData.REFilterStatus || [], "ntransactionstatus", "sfilterstatus", 'descending', "ntransactionstatus", false);
      REJobStatus = constructOptionList(this.props.Login.masterData.REJobStatus || [], 'njobstatuscode', 'sidsjobstatusname', 'ascending', 'njobstatuscode', false);
      Testvalues = constructOptionList(this.props.Login.masterData.Testvalues || [], 'ntestcode', 'stestsynonym', 'ascending', 'ntestcode', false);
      ApprovalConfigVersion = constructOptionList(this.props.Login.masterData.ApprovalConfigVersion || [], 'napprovalconfigversioncode', 'sversionname', 'ascending', 'napprovalconfigversioncode', false);
      skip = this.props.Login.skip === undefined ? skip : this.props.Login.skip;
      take = this.props.Login.take || take;
      testskip = this.props.Login.testskip === undefined ? testskip : this.props.Login.testskip;
      testtake = this.props.Login.testtake || testtake;

      if (this.props.Login.resultDataState && this.props.Login.resultDataState !== previousProps.Login.resultDataState) {
        resultDataState = this.props.Login.resultDataState;
      }

      if (this.props.Login.instrumentDataState && this.props.Login.instrumentDataState !== previousProps.Login.instrumentDataState) {
        instrumentDataState = this.props.Login.instrumentDataState;
      }

      if (this.props.Login.taskDataState && this.props.Login.taskDataState !== previousProps.Login.taskDataState) {
        taskDataState = this.props.Login.taskDataState;
      }

      if (this.props.Login.resultChangeDataState && this.props.Login.resultChangeDataState !== previousProps.Login.resultChangeDataState) {
        resultChangeDataState = this.props.Login.resultChangeDataState;
      }

      if (this.props.Login.historyDataState && this.props.Login.historyDataState !== previousProps.Login.historyDataState) {
        historyDataState = this.props.Login.historyDataState;
      }

      if (this.props.Login.testCommentDataState && this.props.Login.testCommentDataState !== previousProps.Login.testCommentDataState) {
        testCommentDataState = this.props.Login.testCommentDataState;
      } // if (this.props.Login.masterData.DynamicColumns && this.props.Login.masterData.DynamicColumns !== previousProps.Login.masterData.DynamicColumns) {
      //     bool = true;
      //     sampleListColumns = listDataFromDynamicArray(this.props.Login.masterData.DynamicColumns[1], 3);
      //     subSampleListColumns = listDataFromDynamicArray(this.props.Login.masterData.DynamicColumns[2], 3);
      //     testListColumns = listDataFromDynamicArray(this.props.Login.masterData.DynamicColumns[3], 3);
      //     sampleListMainField = listDataFromDynamicArray(this.props.Login.masterData.DynamicColumns[1], 8);
      //     subSampleListMainField = listDataFromDynamicArray(this.props.Login.masterData.DynamicColumns[2], 8);
      //     testListMainField = listDataFromDynamicArray(this.props.Login.masterData.DynamicColumns[3], 8);
      //     SingleItem = listDataFromDynamicArray(this.props.Login.masterData.DynamicColumns[1], 4)
      //     SampleGridItem = listDataFromDynamicArray(this.props.Login.masterData.DynamicColumns[1], 5)
      //     SampleGridExpandableItem = listDataFromDynamicArray(this.props.Login.masterData.DynamicColumns[1], 6)
      //     sampleMoreField = listDataFromDynamicArray(this.props.Login.masterData.DynamicColumns[1], 7)
      //     subSampleMoreField = listDataFromDynamicArray(this.props.Login.masterData.DynamicColumns[2], 7)
      //     testMoreField = listDataFromDynamicArray(this.props.Login.masterData.DynamicColumns[3], 7)
      // }


      let selectFilterStatus = {
        ntransactionstatus: transactionStatus.PARTIAL,
        sfilterstatus: this.props.intl.formatMessage({
          id: "IDS_PARTIAL"
        }),
        scolorhexcode: "#800000"
      };
      const selectedFilters = this.props.Login.masterData.REFilterStatus || [];
      const selectedFiltersNew = JSON.parse(JSON.stringify(selectedFilters));
      const index = selectedFiltersNew.findIndex(item => item.ntransactionstatus === transactionStatus.PARTIAL);

      if (selectedFiltersNew.length > 0 && index === -1) {
        selectedFiltersNew.push(selectFilterStatus);
      }

      selectedFilter = selectedFiltersNew; // const updateInfo = {
      //     typeName: DEFAULT_RETURN,
      //     data: {masterData: this.props.Login.masterData,showTest,showSample }
      // }
      // this.props.updateStore(updateInfo);

      if (bool) {
        this.setState({
          showTest,
          showSample,
          userRoleControlRights,
          controlMap,
          resultDataState,
          instrumentDataState,
          materialDataState,
          taskDataState,
          documentDataState,
          resultChangeDataState,
          testCommentDataState,
          historyDataState,
          sampleListColumns,
          subSampleListColumns,
          testListColumns,
          SingleItem,
          sampleListMainField,
          subSampleListMainField,
          testListMainField,
          SampleGridItem,
          SampleGridExpandableItem,
          sampleMoreField,
          subSampleMoreField,
          testMoreField,
          selectedRecord,
          SampleType: SampleType.get("OptionList"),
          RegistrationType: RegistrationType.get("OptionList"),
          RegistrationSubType: RegistrationSubType.get("OptionList"),
          REFilterStatus: REFilterStatus.get("OptionList"),
          REJobStatus: REJobStatus.get("OptionList"),
          Testvalues: Testvalues.get("OptionList"),
          ApprovalConfigVersion: ApprovalConfigVersion.get("OptionList"),
          validateFormulaMandyFields,
          skip,
          take,
          testskip,
          testtake,
          selectedFilter,
          DynamicSampleColumns,
          DynamicSubSampleColumns,
          DynamicTestColumns,
          DynamicGridItem,
          DynamicGridMoreField
        });
      }
    }

    if (this.props.Login.meanTestParameterList !== previousProps.Login.meanTestParameterList) {
      this.setState({
        meanTestParameterList: this.props.Login.meanTestParameterList
      });
    }
  }

  componentWillUnmount() {
    const updateInfo = {
      typeName: DEFAULT_RETURN,
      data: {
        masterData: [],
        inputParam: undefined
      }
    };
    this.props.updateStore(updateInfo);
  }

  static getDerivedStateFromProps(props, state) {
    if (props.Login.masterStatus !== "") {
      if (props.Login.errorCode === 417 || props.Login.errorCode === 409) {
        toast.warn(props.Login.masterStatus);
        props.Login.masterStatus = "";
      }
    }

    if (props.Login.error !== state.error) {
      toast.error(props.Login.error);
      props.Login.error = "";
    }

    if (props.Login.selectedRecord !== state.selectedRecord) {
      return {
        selectedRecord: { ...state.selectedRecord,
          ...props.Login.selectedRecord
        }
      };
    }

    return null;
  }

}

const mapStatetoProps = state => {
  return {
    Login: state.Login
  };
};

export default connect(mapStatetoProps, {
  crudMaster,
  updateStore,
  getsubSampleREDetail,
  getTestREDetail,
  getTestChildTabREDetail,
  resultGetModule,
  completeTest,
  testMethodSourceEdit,
  addREInstrument,
  deleteInstrumentRecord,
  fetchInstrumentRecord,
  deleteTaskRecord,
  fetchTaskRecord,
  parameterRecord,
  checkListRecord,
  onSaveCheckList,
  defaultTest,
  getFormula,
  getRERegistrationType,
  getRERegistrationSubType,
  getREApprovalConfigVersion,
  getResultEntryDetails,
  calculateFormula,
  getREFilterStatus,
  getREFilterTestData,
  getREJobStatus,
  getSampleChildTabREDetail,
  viewAttachment,
  getAttachmentCombo,
  deleteAttachment,
  resultImportFile,
  validateEsignCredentialComplete,
  getCommentsCombo,
  updateTestMethodSource,
  filterTransactionList,
  validateEsignCredential,
  updateParameterComments,
  previewSampleReport,
  getMeanCalculationTestParameter
})(injectIntl(ResultEntry));

