import DropArea from "./DropArea.jsx";
import { intl } from '../App';
import TrashDropZone from "./TrashDropZone";
import SideBarItem from "./SideBarItem";
import DynamicRow from "./Row";
import {
  handleMoveWithinParent,
  handleMoveToDifferentParent,
  handleMoveSidebarComponentIntoParent,
  handleRemoveItemFromLayout
} from "./helpers";
import { AtAccordion } from '../custom-accordion/custom-accordion.styles';
import { SIDEBAR_ITEM, COMPONENT, COLUMN, COMPONENTROW } from "./constants";
import "./styles.css"
import { Col, Row, Accordion, Card, useAccordionToggle, AccordionToggle, AccordionCollapse, Badge, Button } from "react-bootstrap";
import AccordionContext from "react-bootstrap/AccordionContext";

import DynamicFieldProperties from "./DynamicFieldProperties.jsx";
import { injectIntl } from "react-intl";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {  faLink, faChevronRight , faComments , faPaperclip, faChevronDown, faChevronUp, faCross, faFilter, faWindowClose, faKeyboard, faBuilding, faInfoCircle } from "@fortawesome/free-solid-svg-icons";
import shortid from "shortid";
import { Image } from 'react-bootstrap';

import toggleExpand from '../../assets/image/expand-icon-blue.svg';
import toggleCollapse from '../../assets/image/collapse-icon-blue.svg';
import { MediaHeader } from "../App.styles.jsx";
import ComboFilterProperties from "./ComboFilterProperties.jsx";
import { ReactComponents } from "../Enumeration.js";
import DateFilterProperties from "./DateFilterProperties.jsx";
import AddSynonym from "./AddSynonym.jsx";
import SplitterLayout from "react-splitter-layout";
// import Component from "./Component.jsx";

const Dynamicinput = (props) => {
  const handleDropToTrashBin = useCallback(
    (dropZone, item) => {
      const splitItemPath = item.path.split("-");
      props.validateDelete(handleRemoveItemFromLayout(props.layout, splitItemPath), item.path, true);
      changeActiveTabIndex(false);
    },
    [props.layout]
  );
  const _scrollBarRef = useRef();
  const _scrollBarChildRef = React.useRef(null);
  // let _scrollBarRef;
  const handleDrop = useCallback(
    (dropZone, item) => {
      const splitDropZonePath = dropZone.path.split("-");
      const pathToDropZone = splitDropZonePath.slice(0, -1).join("-");

      let newItem = { id: item.id, type: item.type };
      if (item.type === COLUMN) {
        newItem.children = item.children;
      }
      if (item.type === COMPONENTROW) {
        newItem = { ...item.data, ...newItem }
      }
      if (item.type === COMPONENT) {
        newItem = { ...item.data, type: item.type }
      }
      // sidebar into
      if (item.type === SIDEBAR_ITEM) {
        // 1. Move sidebar item into page
        const newComponent = {
          id: shortid.generate(),
          ...item.component
        };
        const newItem = {
          id: newComponent.id,
          ...item,
          type: COMPONENT,
        };
        props.setLayout(
          handleMoveSidebarComponentIntoParent(
            props.layout,
            splitDropZonePath,
            newItem
          ), dropZone.path
        );
        return;
      }

      // move down here since sidebar items dont have path
      const splitItemPath = item.path.split("-");
      const pathToItem = splitItemPath.slice(0, -1).join("-");

      // 2. Pure move (no create)
      if (splitItemPath.length === splitDropZonePath.length) {
        // 2.a. move within parent
        if (pathToItem === pathToDropZone) {
          props.setLayout(
            handleMoveWithinParent(props.layout, splitDropZonePath, splitItemPath), dropZone.path
          );
          return;
        }

        // 2.b. OR move different parent
        // TODO FIX columns. item includes children
        props.setLayout(
          handleMoveToDifferentParent(
            props.layout,
            splitDropZonePath,
            splitItemPath,
            newItem
          ), dropZone.path
        );
        return;
      }

      // 3. Move + Create
      props.setLayout(
        handleMoveToDifferentParent(
          props.layout,
          splitDropZonePath,
          splitItemPath,
          newItem
        ), dropZone.path
      );
    },
    [props.layout,// components
    ]
  );
  const renderRow = (row, currentPath, index) => {
    return (
      <DynamicRow
        key={row.id}
        data={row}
        handleDrop={handleDrop}
        // components={components}
        baselength={row.children && row.children.length ? row.children.length : 1}
        path={currentPath}
        index={index}
        onclickcomponent={(event, data, path)=> {props.onclickcomponent(event, data, path) ; changeActiveTabIndex(3) } }
        selectedFieldRecord={props.selectedFieldRecord}
      />
    );
  };
  const CustomToggle = ({ children, eventKey }) => {
    const currentEventKey = React.useContext(AccordionContext);
    const isCurrentEventKey = currentEventKey === eventKey;
    const decoratedOnClick = useAccordionToggle(eventKey, "");


    return (
      <div
        className="d-flex justify-content-between accordion-button"
        onClick={decoratedOnClick}>
        {children}
        {isCurrentEventKey ?
          <span className="pr-2"><FontAwesomeIcon icon={faChevronUp} /></span>
          : <span className="pr-2"><FontAwesomeIcon icon={faChevronDown} //onClick={children.props.onExpandCall}
          /></span>}
      </div>
    );
  }


  const [enablePin, setEnablePin] = useState(false);

  //const [splitChangeWidthPercentage, setpaneSizeChange] = useState(87);
  const changePin = () => {
    setEnablePin(!enablePin);
  }
  const [activeTabIndex, setactiveTabIndex] = useState(false);
  const changeActiveTabIndex = (val) => {
    if(val != 3){
      props.hidePropFilter()
      setactiveTabIndex(val);
    }else if(props.selectedFieldRecord && Object.keys(props.selectedFieldRecord).length !== 0){
      props.hidePropFilter()
      setactiveTabIndex(val);
    }
  }


//   const paneSizeChange = (d) => {
//     setpaneSizeChange(d)
// }
  return (
    <div className="vertical-tab-top popup">
      <div className="full-width center-area-wrap">
        {/* <div className="sideBar height-auto">
          <PerfectScrollbar>
            <div className="popup-fixed-full-width">
              <AtAccordion>
                <Accordion defaultActiveKey="InputFields">
                  <CustomToggle eventKey={"InputFields"}>
                    <MediaHeader className="pl-2">{props.intl.formatMessage({ id: "IDS_INPUTFIELDS" })}</MediaHeader>
                  </CustomToggle>
                  <AccordionCollapse eventKey={"InputFields"}>
                    <>
                      {
                        Object.values(props.reactComponents).map((sideBarItem, index) => (
                          <SideBarItem key={sideBarItem.id} data={sideBarItem} displayField={"componentname"} />
                        ))
                      }
                    </>
                  </AccordionCollapse>
                  <CustomToggle eventKey={"PredefinedComponents"}>
                    <MediaHeader className="pl-2">{props.intl.formatMessage({ id: "IDS_COMPONENTS" })}</MediaHeader>
                  </CustomToggle>
                  <AccordionCollapse eventKey={"PredefinedComponents"}>
                    <>
                      {
                        Object.values(props.reactInputFields).map((sideBarItem, index) => (
                          <SideBarItem key={sideBarItem.id} data={sideBarItem} displayField={"label"} />
                        ))
                      }
                    </>
                  </AccordionCollapse>
                </Accordion>
              </AtAccordion>
            </div>
          </PerfectScrollbar>
        </div> */}
        {/* <div> */}
        {/* <SplitterLayout borderColor="#999"
          primaryIndex={1} percentage={true}
          secondaryInitialSize={splitChangeWidthPercentage}
          onSecondaryPaneSizeChange={paneSizeChange}
        // primaryMinSize={40}
        // secondaryMinSize={20}
        > */}
          <div className="center-area full-width">
            <div className="trash">
              <TrashDropZone
                data={props.layout}
                onDrop={handleDropToTrashBin}
              />
            </div>
            <div className="">
              {/* <PerfectScrollbar> */}
              <div className="popup-fixed-center-headed-full-width">
                <div className="page">
                  {props.layout.map((row, index) => {
                    const currentPath = `${index}`;

                    return (
                      <React.Fragment key={row.id}>
                        <DropArea
                          data={{
                            path: currentPath,
                            childrenCount: props.layout.length
                          }}
                          onDrop={handleDrop}
                          path={currentPath}
                        />
                        {renderRow(row, currentPath, index)}
                      </React.Fragment>
                    );
                  })}
                  <DropArea
                    data={{
                      path: `${props.layout.length}`,
                      childrenCount: props.layout.length
                    }}
                    onDrop={handleDrop}
                    isLast
                    isEmpty={props.layout.length ? false : true}
                  />
                </div>
              </div>
              {/* </PerfectScrollbar> */}
            </div>
          </div>
          {/* <div> */}
          {/* <div className="rightBar height-auto"> */}
            {/* <span className="pinned-image" onClick={changePin}> */}
              {/* <Image src={!enablePin ? toggleExpand : toggleCollapse} alt="sidebar" width="24" height="24" /> */}
              {/* {intl.formatMessage({ id: "IDS_PROPERTIES" })} */}
            {/* </span> */}

          {/* </div> */}
          {/* </div> */}
        {/* </SplitterLayout> */}
        {/* </div> */}
      </div>
      <div className={`vertical-tab ${activeTabIndex ? 'active' :""}`} >
          <div className={`vertical-tab-content pager_wrap wrap-class ${activeTabIndex ? 'active' :""}`}>
          {/* <PerfectScrollbar containerRef = {(ref) => { _scrollBarRef.current = ref; }}> */}
          {/* <div className="sm-view-v-t "> */}
              <span className={` vertical-tab-close ${activeTabIndex ? 'active' :""}`} onClick={()=>changeActiveTabIndex(false)}><FontAwesomeIcon icon={faChevronRight} /> </span>
              {_scrollBarRef && _scrollBarRef.current && _scrollBarChildRef && _scrollBarChildRef.current &&  (_scrollBarChildRef.current.clientHeight > _scrollBarRef.current.clientHeight) ? <span className="scroll-up-arrow" onClick={()=> {_scrollBarRef.current.scrollTop = 0;}}>
              <FontAwesomeIcon icon={faChevronUp} />
              </span> :""}
              {activeTabIndex && activeTabIndex == 1 ? 
              <div className={` vertical-tab-content-Inputs sm-view-v-t  ${activeTabIndex && activeTabIndex == 1 ? 'active' :""}`}>
              <h4 className='inner_h4'>
                Input Fields
              </h4>
              <PerfectScrollbar  className="xs-view-v-t" containerRef = {(ref) => { _scrollBarRef.current = ref; }}>

              <div className="flex-wrap"   ref={_scrollBarChildRef}>
              {
                    Object.values(props.reactComponents).map((sideBarItem, index) => (
                      <SideBarItem key={sideBarItem.id} data={sideBarItem} displayField={"componentname"} />
                    ))
                  }
              </div>
              </PerfectScrollbar>                                
          </div>
          
              : ""}
              { activeTabIndex && activeTabIndex == 2 ?
              <div className={` vertical-tab-content-components sm-view-v-t   ${activeTabIndex && activeTabIndex == 2 ? 'active' :""}`}>
              <h4 className='inner_h4'>
                  Components
              </h4>
              <PerfectScrollbar  className="xs-view-v-t" containerRef = {(ref) => { _scrollBarRef.current = ref; }}>
              <div  className="flex-wrap" ref={_scrollBarChildRef}>
                {
                    Object.values(props.reactInputFields).map((sideBarItem, index) => (
                      <SideBarItem key={sideBarItem.id} data={sideBarItem} displayField={"label"} />
                    ))
                  }
              </div>
              </PerfectScrollbar>                                    
          </div>
              : ""}
              
              {activeTabIndex && activeTabIndex == 3 ? 
              <div className={` vertical-tab-content-properties sm-view-v-t  ${activeTabIndex && activeTabIndex == 3 ? 'active' :""}`}>
              <h4 className='inner_h4'>
                  Properties
              </h4>   
              <PerfectScrollbar  className="xs-view-v-t" containerRef = {(ref) => { _scrollBarRef.current = ref; }}>
                      <div  className="flex-wrap" ref={_scrollBarChildRef}>
                        <h5 className="d-flex text-icon-align">
                          <Badge bg="secondary">{props.selectedFieldRecord.label ? props.selectedFieldRecord.label : props.selectedFieldRecord.componentname
                            ? props.selectedFieldRecord.componentname : ""}</Badge>
                          {props.showsynonym ?
                            <Button className="btn-light nobg" onClick={(e) => props.addSynonym()} >
                              <FontAwesomeIcon icon={faWindowClose} />
                            </Button>
                            : (props.selectedFieldRecord.label || props.selectedFieldRecord.componentname) &&
                              (props.selectedFieldRecord.componentcode === ReactComponents.COMBO ||
                                props.selectedFieldRecord.componentcode === ReactComponents.DATE) ?
                              <Button className="btn-light nobg" onClick={() => props.showPropFilter()} >
                                {props.showFilter ?
                                  <FontAwesomeIcon icon={faWindowClose} />// style={{ fontSize: "13px" }} />
                                  :
                                  <FontAwesomeIcon icon={faFilter} style={{
                                    fontSize: "13px", color: `${(props.selectedFieldRecord.conditionArrayUI && props.selectedFieldRecord.conditionArrayUI.length > 0) ||
                                      (props.selectedFieldRecord.dateConstraintArraySQL && props.selectedFieldRecord.dateConstraintArraySQL.length > 0) ? "#0470fbb6" : "#212529"}`
                                  }} />
                                }
                              </Button>
                              : ""
                          }
                        </h5>
                        <div className="secondary-warp">
                        {
                          props.showsynonym ?
                            <AddSynonym
                              selectedFieldRecord={props.selectedFieldRecord}
                              onInputOnChange={props.onInputOnChange}
                              languages={props.languages}
                              addSynonym={props.addSynonym}
                              language={props.language}
                            />
                            : props.showFilter ?
                              props.selectedFieldRecord.componentcode === ReactComponents.COMBO ?
                                <ComboFilterProperties
                                  onInputOnChange={props.onInputOnChange}
                                  onNumericInputChange={props.onNumericInputChange}
                                  selectedFieldRecord={props.selectedFieldRecord}
                                  tables={props.ReactTables}
                                  numericConditions={props.numericConditions}
                                  stringConditions={props.stringConditions}
                                  onComboChange={props.onComboChange}
                                  tableColumn={props.filterColumns}
                                  filterData={props.filterData || []}
                                  inputFields={props.inputFields}
                                  addChildMapping={props.addChildMapping}
                                  valueMembers={props.valueMembers}
                                  addTag={props.addTag}
                                  addCondition={props.addCondition}
                                  deleteCondition={props.deleteCondition}
                                />
                                :
                                <DateFilterProperties
                                  onInputOnChange={props.onInputOnChange}
                                  onNumericInputChange={props.onNumericInputChange}
                                  selectedFieldRecord={props.selectedFieldRecord}
                                  tables={props.ReactTables}
                                  dateConditions={props.dateConditions}
                                  onComboChange={props.onComboChange}
                                  dateComponents={props.dateComponents}
                                  filterData={props.filterData || []}
                                  inputFields={props.inputFields}
                                  addChildMapping={props.addChildMapping}
                                  valueMembers={props.valueMembers}
                                  addTag={props.addTag}
                                  addDateConstraints={props.addDateConstraints}
                                  deleteCondition={props.deleteDateCondition}
                                  userinfo={props.userinfo}
                                  CurrentTime={new Date()}
                                  handleDateChange={props.handleDateChange}
                                  period={props.period}
                                />
                              :
                              <DynamicFieldProperties
                                onInputOnChange={props.onInputOnChange}
                                onNumericInputChange={props.onNumericInputChange}
                                selectedFieldRecord={props.selectedFieldRecord}
                                tables={props.ReactTables}
                                onComboChange={props.onComboChange}
                                tableColumn={props.tableColumn}
                                inputFields={props.inputFields}
                                addChildMapping={props.addChildMapping}
                                valueMembers={props.valueMembers}
                                addTag={props.addTag}
                                addSynonym={props.addSynonym}
                                parentRadioValue={props.parentRadioValue}
                              />
                        }
                        </div>

                      </div>
              </PerfectScrollbar>
          </div>
              : ""}
              
              {/* </div> */}
              {/* </PerfectScrollbar> */}
              {_scrollBarRef && _scrollBarRef.current && _scrollBarChildRef && _scrollBarChildRef.current &&  (_scrollBarChildRef.current.clientHeight > _scrollBarRef.current.clientHeight) ? <span  className="scroll-down-arrow" onClick={()=> {_scrollBarRef.current.scrollTop = 400;}}>
              <FontAwesomeIcon icon={faChevronDown} />

              </span> :"" }
          </div>

          <div className='tab-head'>
              <ul>
                  <li className={`${activeTabIndex && activeTabIndex == 1 ? 'active' :""}`} onClick={()=>changeActiveTabIndex(1)}>
                  <FontAwesomeIcon icon={faKeyboard} />
                      <span>Input Fields</span>
                  </li>
                  <li className={`${activeTabIndex && activeTabIndex == 2 ? 'active' :""}`} onClick={()=>changeActiveTabIndex(2)}>
                  <FontAwesomeIcon icon={faBuilding} />
                      <span>Components</span>
                  </li>
                  <li className={`${(props.selectedFieldRecord && Object.keys(props.selectedFieldRecord).length === 0) ? "disabled" : activeTabIndex && activeTabIndex == 3 ? 'active' :""}`} onClick={()=>changeActiveTabIndex(3)}>
                  <FontAwesomeIcon icon={faInfoCircle } />
                      <span> Properties </span>
                  </li>
              </ul>
          </div>
      </div>
    </div>
  );
};
export default injectIntl(Dynamicinput);
