export function getReleasedRegistrationType(inputData) {
  return function (dispatch) {
    dispatch(initRequest(true));
    rsapi.post("release/getRegistrationType", inputData).then(response => {
      let responseData = { ...response.data
      };
      responseData = sortData(responseData);
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          masterData: { ...inputData.masterData,
            ...responseData,
            SampleTypeValue: inputData.SampleTypeValue
          },
          loading: false
        }
      });
    }).catch(error => {
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false
        }
      });

      if (error.response.status === 500) {
        toast.error(error.message);
      } else {
        toast.warn(error.response.data);
      }
    });
  };
}
