export function generateReleasedReport(inputData, Data, Preview) {
  return function (dispatch) {
    dispatch(initRequest(true)); // const inputData = {
    //     npreregno: inputParam.npreregno,
    //     ntransactionsamplecode: inputParam.ntransactionsamplecode,
    //     ntransactiontestcode: inputParam.ntransactiontestcode,
    //     url: inputParam.url,
    //     nsampletypecode: inputParam.nsampletypecode,
    //     nprimarykey: inputParam.npreregno,
    //     ncoareporttypecode: reportCOAType.SAMPLEWISE,
    //     nreporttypecode: REPORTTYPE.COAREPORT,
    //     sprimarykeyname: "npreregno",
    //     ncontrolcode: inputParam.ncontrolCode,
    //     nregtypecode: inputParam.nregtypecode,
    //     nregsubtypecode: inputParam.nregsubtypecode,
    //     ntransactiontestcode:inputParam.ntransactiontestcode,
    //     napproveconfversioncode: inputParam.napprovalversioncode,
    //     action: inputParam.action,
    //     ncoaparentcode: inputParam.ncoaparentcode,
    //     ntransactionstatus:inputParam.ntransactionstatus,
    //     Preview: Preview,
    // }

    rsapi.post("release/updateRelease", inputData).then(response => {
      if (response.data["PortalStatus"] && response.data["PortalStatus"].length > 0) {
        dispatch(UpdateExternalOrderStatus(response.data["PortalStatus"], inputData));
      }

      let masterData = { ...Data,
        ...response.data
      };

      if (response.data.rtn === "Success") {
        document.getElementById("download_data").setAttribute("href", response.data.filepath);
        document.getElementById("download_data").click();
      } else {
        toast.warn(response.data.rtn);
      }

      sortData(masterData.ReleaseHistory, "", 'ncoaparentcode');
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false,
          change: inputData.change,
          masterData,
          modalShow: false,
          selectedRecord: {},
          loadEsign: false
        }
      });
    }).catch(error => {
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false
        }
      });

      if (error.response.status === 500) {
        toast.error(error.message);
      } else {
        toast.warn(error.response.data);
      }
    });
  };
}
