                                      ? "-"
                                      : this.props.Login.masterData
                                        .selectedInstrument.sremarks}
                                  </ReadOnlyText>
                                </FormGroup>
                              </Col>
                            </Row>
                            {selectedInstrument && (
                              <CustomTab
                                tabDetail={this.tabDetail()}
                                onTabChange={this.onTabChange}
                              />
                            )}
                          </Card.Body>
                        </>
                      ) : (
                        ""
                      )}
                    </Card>
