export function getTreeByProduct(Map, selectedRecord, comboData, inputParam) {
  return function (dispatch) {
    dispatch(initRequest(true));
    rsapi.post("/registration/getTreeByProduct", Map).then(response => {
      let {
        Specification,
        AgaramTree,
        ActiveKey,
        FocusKey,
        OpenNodes
      } = [];
      const selectedSpec = {};

      if (response.data["rtn"] === true) {
        Specification = constructOptionList(response.data["Specification"] || [], "nallottedspeccode", "sspecname", undefined, undefined, true).get("OptionList");
        AgaramTree = response.data["AgaramTree"];
        ActiveKey = response.data["ActiveKey"];
        FocusKey = response.data["FocusKey"];
        OpenNodes = response.data["OpenNodes"]; //Manufacturer = response.data["Manufacturer"];

        selectedSpec["nallottedspeccode"] = Specification.length > 0 ? {
          "value": Specification[0].value,
          "label": Specification[0].label,
          "item": Specification[0].item
        } : "";
        selectedSpec["sversion"] = Specification.length > 0 ? Specification[0].item.sversion : "";
        selectedSpec["ntemplatemanipulationcode"] = Specification.length > 0 ? Specification[0].item.ntemplatemanipulationcode : -1;
      }

      selectedRecord = { ...selectedRecord,
        ...selectedSpec
      };
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          Specification,
          selectedRecord,
          AgaramTree,
          ActiveKey,
          FocusKey,
          OpenNodes,
          Test: [],
          SelectedTest: [],
          loading: false,
          comboData,
          ...inputParam,
          selectedSpec,
          Component: [],
          selectComponent: {},
          selectedComponent: {}
        }
      });
    }).catch(error => {
      toast.error(error.message);
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false
        }
      });
    });
  };
}
export const getNewRegSpecification = (inputParam, masterData) => {
  return dispatch => {
