        if (inputData[dataStateName] && masterData[activeName].length <= inputData[dataStateName].skip) {
          skipInfo = { ...skipInfo,
            [dataStateName]: { ...inputData[dataStateName],
              skip: 0,
              sort: undefined,
              filter: undefined
            }
          };
        } else {
          skipInfo = { ...skipInfo,
            [dataStateName]: { ...inputData[dataStateName],
              sort: undefined,
              filter: undefined
            }
          };
        }

        dispatch({
          type: DEFAULT_RETURN,
          payload: {
            masterData,
            loading: false,
            showFilter: false,
            activeSampleTab: inputData.activeSampleTab,
            activeTestTab: inputData.activeTestTab,
            skip: undefined,
            take: undefined,
            ...skipInfo,
            inputParam
          }
        });
      }).catch(error => {
        dispatch({
          type: DEFAULT_RETURN,
          payload: {
            loading: false
          }
        });

        if (error.response.status === 500) {
          toast.error(error.message);
        } else {
          toast.warn(error.response.data);
        }
      });
    } else {
      let oldSelectedTest = inputData.masterData.selectedTest;
      let TestSelected = getRecordBasedOnPrimaryKeyName(inputData.masterData.selectedTest, inputData.removeElementFromArray[0].npreregno, "npreregno");
      let isGrandChildGetRequired = false;

      if (TestSelected.length > 0) {
        isGrandChildGetRequired = true;
      } else {
        isGrandChildGetRequired = false;
      }

      fillRecordBasedOnCheckBoxSelection(inputData.masterData, inputData.selectedSample, inputData.childTabsKey, inputData.checkBoxOperation, "ntransactionsamplecode", inputData.removeElementFromArray);

      if (isGrandChildGetRequired) {
        let ntransactiontestcode = inputData.masterData.RegistrationGetTest.length > 0 ? inputData.masterData.RegistrationGetTest[0].ntransactiontestcode.toString() : "-1";
        let selectedSubSample = inputData.selectedSubSample; // let selectedPreregno = inputData.npreregno;

        let selectedTest = inputData.masterData.RegistrationGetTest.length > 0 ? [inputData.masterData.RegistrationGetTest[0]] : []; // let selectedSubSample = inputData.masterData.RegistrationGetSubSample

        let masterData = { ...inputData.masterData,
          selectedSubSample,
          selectedTest
        };
        inputData = { ...inputData,
          childTabsKey: ["RegistrationTestComment", "RegistrationParameter"],
          ntransactiontestcode,
          masterData,
          selectedTest,
          selectedSubSample,
          checkBoxOperation: 3
        };
        dispatch(getTestChildTabDetailRegistration(inputData, true));
      } else {
        let masterData = { ...inputData.masterData,
          selectedSubSample: inputData.selectedSubSample,
          selectedTransactioncode: inputData.ntransactionsamplecode,
          selectedTest: inputData.masterData.RegistrationGetTest.length > 0 ? [inputData.masterData.RegistrationGetTest[0]] : []
        };
        const wholeTestList = masterData.RegistrationGetTest.map(b => b.ntransactiontestcode);
        oldSelectedTest.forEach((test, index) => {
          if (!wholeTestList.includes(test.ntransactiontestcode)) {
            oldSelectedTest.splice(index, 1);
          }

          return null;
        });
        let keepOld = false;
        let ntransactiontestcode;

        if (oldSelectedTest.length > 0) {
          keepOld = true;
          masterData = { ...masterData,
            selectedTest: oldSelectedTest
          };
        } else {
          ntransactiontestcode = inputData.masterData.RegistrationGetTest.length > 0 ? inputData.masterData.RegistrationGetTest[0].ntransactiontestcode : "-1";
        }

        masterData["RegistrationTestComment"] = keepOld ? inputData.masterData.RegistrationTestComment ? inputData.masterData.RegistrationTestComment : [] : getRecordBasedOnPrimaryKeyName(inputData.masterData.RegistrationTestComment, ntransactiontestcode, "ntransactiontestcode");
        masterData["RegistrationParameter"] = keepOld ? inputData.masterData.RegistrationParameter ? inputData.masterData.RegistrationParameter : [] : getRecordBasedOnPrimaryKeyName(inputData.masterData.RegistrationParameter, ntransactiontestcode, "ntransactiontestcode");
        let skipInfo = {};
        let dataStateArray = [{
          activeName: 'selectedSample',
          dataStateName: 'sampleGridDataState'
        }, {
          activeName: 'RegistrationSourceCountry',
          dataStateName: 'sourceDataState'
        }, {
          activeName: 'RegistrationTestComment',
          dataStateName: 'testCommentDataState'
        }, {
          activeName: 'RegistrationParameter',
          dataStateName: 'resultDataState'
        }];
        dataStateArray.map(arr => {
          if (inputData[arr.dataStateName] && masterData[arr.activeName] && masterData[arr.activeName].length <= inputData[arr.dataStateName].skip) {
            skipInfo = { ...skipInfo,
              [arr.dataStateName]: { ...inputData[arr.dataStateName],
                skip: 0,
                sort: undefined,
                filter: undefined
              }
            };
          } else {
            skipInfo = { ...skipInfo,
              [arr.dataStateName]: { ...inputData[arr.dataStateName],
                sort: undefined,
                filter: undefined
              }
            };
          }

          return null;
        });
        dispatch({
          type: DEFAULT_RETURN,
          payload: {
            masterData,
            loading: false,
            showFilter: false,
            activeSampleTab: inputData.activeSampleTab,
            activeTestTab: inputData.activeTestTab,
            ...skipInfo
          }
        });
      }
    }
  };
}
export function acceptRegistration(inputParam, LoginProps) {
  return function (dispatch) {
    dispatch(initRequest(true));
    rsapi.post("/registration/acceptRegistration", inputParam.inputData).then(response => {
      if (response.data.rtn === undefined || response.data.rtn === "Success" || response.data.rtn === "IDS_ATLEASTONETESTMUSTBEPREREGISTER" || response.data.rtn === "IDS_ALLSAMPLESAREREGISTERED" || response.data.rtn === "IDS_SELECTPREREGISTERORQUARANTINESAMPLES") {
        replaceUpdatedObject(response.data["RegistrationGetSample"], LoginProps.RegistrationGetSample, "npreregno");
        replaceUpdatedObject(response.data["RegistrationGetSubSample"], LoginProps.RegistrationGetSubSample, "ntransactionsamplecode");
        replaceUpdatedObject(response.data["RegistrationGetTest"], LoginProps.RegistrationGetTest, "ntransactiontestcode");
        delete response.data["RegistrationGetSample"];
        delete response.data["RegistrationGetSubSample"];
        delete response.data["RegistrationGetTest"];
        let masterData = { ...LoginProps,
          ...response.data,
          selectedSample: replaceUpdatedObject(response.data["selectedSample"], LoginProps.selectedSample, "npreregno"),
          selectedSubSample: replaceUpdatedObject(response.data["selectedSubSample"], LoginProps.selectedSubSample, "ntransactionsamplecode"),
          selectedTest: replaceUpdatedObject(response.data["selectedTest"], LoginProps.selectedTest, "ntransactiontestcode")
        };
        let respObject = {
          masterData,
          ...inputParam.inputData,
          loading: false,
          loadEsign: false,
          openModal: false,
          showSample: undefined
        };
        dispatch(postCRUDOrganiseTransSearch(inputParam.postParamList, respObject));

        if (response.data.rtn === "IDS_ATLEASTONETESTMUSTBEPREREGISTER" || response.data.rtn === "IDS_ALLSAMPLESAREREGISTERED" || response.data.rtn === "IDS_SELECTPREREGISTERORQUARANTINESAMPLES") {
          toast.warn(intl.formatMessage({
            id: response.data.rtn
          }));
        }
      } else {
        dispatch({
          type: DEFAULT_RETURN,
          payload: {
            loading: false,
            loadEsign: false,
            openModal: false
          }
        });
        toast.warn(response.data.rtn);
      }
    }).catch(error => {
      toast.error(error.message);
    });
  };
}
export const addMoreTest = (inputParam, ncontrolCode) => {
  return dispatch => {
    let {
      sampleList
    } = inputParam;
    let value = false;

    if (inputParam.selectedSubSample && inputParam.selectedSubSample.length > 0) {
      value = inputParam.selectedSubSample.some(obj => obj.nspecsampletypecode !== inputParam.selectedSubSample[0].nspecsampletypecode);

      if (value) {
        return toast.warn(intl.formatMessage({
          id: "IDS_PLEASESELECTSAMPLEWITHSAMESPECANDCOMPONENT"
        }));
      }

      sampleList = sampleList ? [...sampleList.slice(inputParam.skip, inputParam.take)] : [];
      const selectedsample = getSameRecordFromTwoArrays(sampleList, inputParam.selectedSample, "npreregno"); // const selectsubsample = getSameRecordFromTwoArrays(sampleList, inputParam.selectedsubsample, "npreregno");

      if (selectedsample && selectedsample.length > 0) {
        const findTransactionStatus = [...new Set(selectedsample.map(item => item.ntransactionstatus))];

        if (findTransactionStatus.length === 1) {
          if (findTransactionStatus[0] !== transactionStatus.REJECT && findTransactionStatus[0] !== transactionStatus.CANCELLED) {
            if (findTransactionStatus[0] !== transactionStatus.CERTIFIED && findTransactionStatus[0] !== transactionStatus.SENT) {
              const findApprovalVersion = [...new Set(selectedsample.map(item => item.napprovalversioncode))];

              if (findApprovalVersion.length === 1) {
                const findSampleSpec = [...new Set(selectedsample.map(item => item.nallottedspeccode))]; //const findComponent = [...new Set(selectsubsample.map(item => item.ncomponentcode))];

                if (findSampleSpec.length === 1) //&& findComponent.length === 1 
                  {
                    inputParam["snspecsampletypecode"] = inputParam.selectedSubSample && [...new Set(inputParam.selectedSubSample.map(x => x.nspecsampletypecode))].join(",");
                    dispatch(initRequest(true));
                    rsapi.post("/registration/getMoreTest", { ...inputParam
                    }).then(response => {
                      dispatch({
                        type: DEFAULT_RETURN,
                        payload: {
                          availableTest: response.data,
                          loadChildTest: true,
                          loading: false,
                          ncontrolCode,
                          screenName: "IDS_TEST",
                          operation: "create",
                          openModal: true,
                          parentPopUpSize: "lg",
                          selectedRecord: {},
                          showSample: undefined
                        }
                      });
                    }).catch(error => {
                      dispatch({
                        type: DEFAULT_RETURN,
                        payload: {
                          loading: false
                        }
                      });

                      if (error.response.status === 500) {
                        toast.error(error.message);
                      } else {
                        toast.warn(this.props.formatMessage({
                          id: error.response.data
                        }));
                      }
                    });
                  } else {
                  toast.warn(intl.formatMessage({
                    id: "IDS_PLEASESELECTSAMPLEWITHSAMESPECANDCOMPONENT"
                  }));
                }
              } else {
                toast.warn(intl.formatMessage({
                  id: "IDS_PLEASESELECTSAMPLEWITHSAMEAPPROVALCONFIG"
                }));
              }
            } else {
              toast.warn(intl.formatMessage({
                id: "IDS_TESTCANNOTBEFORCERTIFIEDSAMPLES"
              }));
            }
          } else {
            ////     toast.warn(intl.formatMessage({ id: "IDS_TESTCANNOTBEFORCANCELLEDREJECTSAMPLES" }));
            toast.warn(intl.formatMessage({
              id: "IDS_SAMPLEISREJECTEDORCANCELLED"
            }));
          }
        } else {
          toast.warn(intl.formatMessage({
            id: "IDS_PLEASESELECTSAMPLEWITHSAMESTATUS"
          }));
        }
      } else {
        toast.warn(intl.formatMessage({
          id: "IDS_SELECTSAMPLE"
        }));
      }
    } else {
      toast.warn(intl.formatMessage({
        id: "IDS_SELECTSUBSAMPLE"
      }));
    }
  };
};
