        masterData,
        openModal: false,
        loadEsign: false,
        loading: false,
        showSample: undefined,
        selectedRecord: {},
        loadRegSubSample: false
      };
      inputParam.postParamList[0]['clearFilter'] = 'yes';
      dispatch(postCRUDOrganiseTransSearch(inputParam.postParamList, respObject)); //    dispatch({
      //     type: DEFAULT_RETURN,
      //     payload: {
      //         masterData,
      //         openModal: false,
      //         loadEsign: false,
      //         loading: false,
      //         showSample: undefined,
      //         selectedRecord: {},
      //         loadRegSubSample:false
      //     }
      // });
    }).catch(error => {
      // console.log(error);
      toast.error(error.message);
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false,
          showConfirmAlert: false
        }
      });
    });
  };
}
export function getEditSubSampleComboService(inputParam, columnList, selectedRecord1, childColumnList, comboComponents, withoutCombocomponent, specBasedComponent) {
  return function (dispatch) {
    dispatch(initRequest(true));
    const {
      userInfo,
      operation
    } = { ...inputParam
    };
    const ntransactionsamplecode = inputParam.mastertoedit[inputParam.primaryKeyName];
    let urlArray = [];
    const timeZoneService = rsapi.post("timezone/getTimeZone"); // const actualService = rsapi.post("dynamicpreregdesign/getComboValues", {
    //     parentcolumnlist: columnList,
    //     childcolumnlist: childColumnList,
    //     userinfo: userInfo
    // })

    const selectedRegistration = rsapi.post("/registration/getEditRegistrationSubSampleDetails", { ...inputParam.editSubSampleRegParam,
      ntransactionsamplecode,
      parentcolumnlist: columnList,
      childcolumnlist: childColumnList,
      userinfo: userInfo
    });
    urlArray = [timeZoneService, selectedRegistration]; // if(specBasedComponent){
    //     const ComponentTestBySpec = rsapi.post("/registration/getComponentBySpec", {
    //         ...Map,
    //         specBasedComponent: specBasedComponent
    //     })
    //     urlArray.push(ComponentTestBySpec) 
    // }

    Axios.all(urlArray).then(response => {
      let selectedRecord = { ...response[1].data["EditData"]
      };
      selectedRecord = { ...selectedRecord,
        ...selectedRecord['jsondata']
      };
      const timeZoneMap = constructOptionList(response[0].data || [], "ntimezonecode", "stimezoneid", undefined, undefined, true);
      const timeZoneList = timeZoneMap.get("OptionList");
      const defaultTimeZone = {
        label: userInfo.stimezoneid,
        value: userInfo.ntimezonecode
      };

      if (selectedRecord.ntransactionstatus !== transactionStatus.PREREGISTER) {
        toast.warn(intl.formatMessage({
          id: "IDS_SELECTPREREGISTERSUBSAMPLETOEDIT"
        }));
        dispatch({
          type: DEFAULT_RETURN,
          payload: {
            loading: false
          }
        });
      } else {
        //               let lstComponent=[]
        if (specBasedComponent) {
          selectedRecord["ncomponentcode"] = {
            label: selectedRecord["scomponentname"],
            value: selectedRecord["ncomponentcode"]
          };
        }

        const languagetypeCode = undefined;
        const comboData = response[1].data;
        delete comboData['EditData'];
        let comboValues = {};

        if (columnList.length > 0) {
          columnList.map(x => {
            if (x.inputtype === 'combo') {
              if (comboData[x.label] && comboData[x.label].length > 0) //&& comboData[x.label][0].hasOwnProperty(x.source) 
                {
                  if (comboData[x.label].length > 0) {
                    if (comboData[x.label][0].label === undefined) {
                      const optionList = constructjsonOptionList(comboData[x.label] || [], x.valuemember, x.displaymember, false, false, true, undefined, x.source, x.isMultiLingual, languagetypeCode);
                      comboData[x.label] = optionList.get("OptionList");
                    } else {
                      comboData[x.label] = comboData[x.label];
                      const optionList = constructjsonOptionDefault(comboData[x.label] || [], x.valuemember, x.displaymember, false, false, true, undefined, x.source, x.isMultiLingual, languagetypeCode);
                    }
                  } else {
                    comboData[x.label] = [];
                  } //comboValues = childComboLoad(x, comboData, selectedRecord, 
                  //   childColumnList, withoutCombocomponent)\


                  comboValues = childComboLoadForEdit(x, comboData, selectedRecord, childColumnList, withoutCombocomponent);
                } else {
                comboValues = {
                  "comboData": comboData
                };
              }
            } else {
              comboValues = {
                comboData: comboData,
                ...comboValues
              };
            }
          });
        } else {
          comboValues = {
            "comboData": comboData
          };
        }

        withoutCombocomponent.map(date => {
          if (date.inputtype === 'date') selectedRecord[date.label] = selectedRecord[date.label] && selectedRecord[date.label] !== '-' ? rearrangeDateFormat(userInfo, selectedRecord[date.label]) : "";
        });
        selectedRecord = { ...selectedRecord
        };
        dispatch({
          type: DEFAULT_RETURN,
          payload: {
            operation,
            timeZoneList,
            defaultTimeZone,
            selectedRecord,
            openModal: true,
            ncontrolCode: inputParam.editSubSampleRegParam.ncontrolCode,
            parentPopUpSize: "lg",
            loading: false,
            showSample: undefined,
            regSubSamplecomboData: comboValues.comboData,
            loadRegSubSample: true,
            screenName: "IDS_SUBSAMPLE",
            operation: "update",
            specBasedComponent //  regRecordToEdit:recordToEdit

          }
        });
      }
    }).catch(error => {
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false
        }
      });

      if (error.response.status === 500) {
        toast.error(error.message);
      } else {
        toast.warn(intl.formatMessage({
          id: error.response.data
        }));
      }
    });
  };
}
