        const inputParam = {
            inputData: {
                file: filedata,
                userinfo: this.props.Login.userInfo
            },
            classUrl: "attachment",
            operation: "view",
            methodUrl: this.props.methodUrl,
            screenName: this.props.Login.screenName
        }
