export function parentChildComboLoad(columnList, comboData, selectedRecord, childColumnList, withoutCombocomponent, ParentComboValues, languagetypeCode) {
  let comboValues = {}
  if (columnList.length > 0) {
    columnList.map(x => {
      if (x.inputtype === 'combo') {
        if (comboData[x.label] && comboData[x.label].length > 0) //&& comboData[x.label][0].hasOwnProperty(x.source) 
        {
          selectedRecord[x.label] = undefined;
          if (comboData[x.label].length > 0) {
            if (comboData[x.label][0].label === undefined) {
              const optionList = constructjsonOptionList(comboData[x.label] || [], x.valuemember,
                x.displaymember, false, false, true, undefined, x.source, x.isMultiLingual, languagetypeCode)
              comboData[x.label] = optionList.get("OptionList");
              if (optionList.get("DefaultValue") !== undefined) {
                selectedRecord[x.label] = optionList.get("DefaultValue")
              }
            } else {
              comboData[x.label] = comboData[x.label]
              const optionList = constructjsonOptionDefault(comboData[x.label] || [], x.valuemember,
                x.displaymember, false, false, true, undefined, x.source, x.isMultiLingual, languagetypeCode)
              if (optionList.get("DefaultValue") !== undefined) {
                selectedRecord[x.label] = optionList.get("DefaultValue")
              }
            }
          } else {
            comboData[x.label] = []
          }
          comboValues = childComboLoad(x, comboData, selectedRecord, childColumnList, withoutCombocomponent, languagetypeCode)
        } else {
          comboValues = {
            "comboData": comboData,
            "selectedRecord": selectedRecord
          }
        }
      } else {
        if(x.isMultiLingual){
        selectedRecord = {
          ...selectedRecord,
          [x.label]: ParentComboValues.item.jsondata ?
            ParentComboValues.item.jsondata[x.displaymember][languagetypeCode] : ParentComboValues.item[x.displaymember][languagetypeCode] || ""
        }
      }else{
        selectedRecord = {
          ...selectedRecord,
          [x.label]: ParentComboValues.item.jsondata ?
            ParentComboValues.item.jsondata[x.displaymember] : ParentComboValues.item[x.displaymember] || ""
        }
      }
        comboValues = {
          comboData: comboData,
          ...comboValues,
          selectedRecord
        }
        // comboValues["selectedRecord"][x.label] = ParentComboValues[x.displaymember] ||""
      }
    })
  }
  else {
    comboValues = {
      "comboData": comboData,
      "selectedRecord": selectedRecord
    }
  }

  return comboValues;
}


export function childComboLoad(x, comboData, selectedRecord, childColumnList, withoutCombocomponent, languagetypeCode) {
