  findMandatoryFields(screenName, selectedRecord, operation) {
    let mandatoryFields = [];

    if (screenName === "IDS_QUOTATION") {
      mandatoryFields = [{
        "idsName": "IDS_CLIENTCATEGORY",
        "dataField": "nclientcatcode",
        "width": "200px",
        "mandatory": true,
        "mandatoryLabel": "IDS_SELECT",
        "controlType": "selectbox"
      }, {
        "idsName": "IDS_CLIENT",
        "dataField": "nclientcode",
        "width": "200px",
        "mandatory": true,
        "mandatoryLabel": "IDS_SELECT",
        "controlType": "textbox"
      }, {
        "idsName": "IDS_CLIENTADDRESS",
        "dataField": "sclientsiteaddress",
        "width": "200px",
        "mandatory": true,
        "mandatoryLabel": "IDS_ENTER",
        "controlType": "textbox"
      }, {
        "idsName": "IDS_INVOICEADDRESS",
        "dataField": "sinvoiceaddress",
        "width": "200px",
        "mandatory": true,
        "mandatoryLabel": "IDS_ENTER",
        "controlType": "selectbox"
      }, //      { "idsName": "IDS_QUOTATIONTYPE", "dataField": "nquotationtypecode", "width": "200px", "mandatory": true, "mandatoryLabel": "IDS_SELECT", "controlType": "selectbox" },
      {
        "idsName": "IDS_PROJECTTYPE",
        "dataField": "nprojecttypecode",
        "width": "200px",
        "mandatory": true,
        "mandatoryLabel": "IDS_SELECT",
        "controlType": "selectbox"
      }, {
        "idsName": "IDS_PROJECTCODE",
        "dataField": "nprojectmastercode",
        "width": "200px",
        "mandatory": true,
        "mandatoryLabel": "IDS_SELECT",
        "controlType": "selectbox"
      }, {
        "idsName": "IDS_PROJECTTITLE",
        "dataField": "sprojecttitle",
        "width": "200px",
        "mandatory": true,
        "mandatoryLabel": "IDS_ENTER",
        "controlType": "selectbox"
      }, {
        "idsName": "IDS_RFWDATE",
        "dataField": "srfwdate",
        "width": "200px",
        "mandatory": true,
        "mandatoryLabel": "IDS_CHOOSE",
        "controlType": "textbox"
      }, //     { "idsName": "IDS_ROLE", "dataField": "suserrolename", "width": "200px", "mandatory": true, "mandatoryLabel": "IDS_ENTER", "controlType": "selectbox" },
      {
        "idsName": "IDS_USER",
        "dataField": "susername",
        "width": "200px",
        "mandatory": true,
        "mandatoryLabel": "IDS_ENTER",
        "controlType": "textbox"
      }, {
        "idsName": "IDS_QUOTATIONDATE",
        "dataField": "dquotationdate",
        "width": "200px",
        "mandatory": true,
        "mandatoryLabel": "IDS_CHOOSE",
        "controlType": "selectbox"
      }]; // mandatoryFields.forEach(item => item.mandatory === true && mandatoryFields.push(item));

      return mandatoryFields;
    } else {
      return [];
    }
  }

  componentDidUpdate(previousProps) {
    let {
      selectedRecord,
      userRoleControlRights,
      controlMap,
      filterData
    } = this.state;

    if (this.props.Login.selectedRecord !== previousProps.Login.selectedRecord) {
      selectedRecord = this.props.Login.selectedRecord;
      this.setState({
        selectedRecord
      });
    }

    if (this.props.Login.userInfo.nformcode !== previousProps.Login.userInfo.nformcode) {
      userRoleControlRights = [];

      if (this.props.Login.userRoleControlRights) {
        this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode] && Object.values(this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode]).map(item => userRoleControlRights.push(item.ncontrolcode));
      }

      controlMap = getControlMap(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode);
      this.setState({
        controlMap,
        userRoleControlRights
      });
    }

    if (this.props.Login.masterData !== previousProps.Login.masterData) {
      this.setState({
        selectedRecord
      });
      this.setState({
        filterData
      });
    }
  }

}

export default connect(mapStateToProps, {
  callService,
  crudMaster,
  filterColumnData,
  updateStore,
  validateEsignCredential,
  getQuotation,
  getProjectTypeComboService,
  viewAttachment,
  addQuotation,
  getreloadQuotation,
  getQuotationAddTestService,
  getQuotationPricingEditService,
  GrossQuotation,
  getClientCategoryComboService,
  getQuotaionClientComboService,
  getProjectCodeComboService
})(injectIntl(Quotation));

