  onSaveQuotationTest(saveType, formRef) {
    let inputData = [];
    inputData["userinfo"] = this.props.Login.userInfo;
    // inputData["supplier"] = this.props.masterData.SelectedSupplier;
  //  inputData["Quotation"] = {};


    let testArray = []
    testArray = this.state.selectedRecord.ntestcode.map(item => {
