                 QuotationTestList={this.props.Login.QuotationTestList}
                 selectedRecord={this.state.selectedRecord || {}}
                 onComboChange={this.onComboChange}

                 /> : 
                 
                 this.props.Login.screenName === "IDS_PRICE" ?

                 <EditQuotationTestPricing
                 onnetAmountEvent={this.onnetAmountEvent}
                 selectedRecord={this.state.selectedRecord || {}}
                 onInputOnChange={this.onInputOnChange}
                 />
                :  this.props.Login.screenName === "IDS_CALCULATEPRICING" ?

                <AddGrossQuotation

                  DiscountBand={this.props.Login.DiscountBand}
                  VATBand={this.props.Login.VATBand} 
                  selectedRecord={this.props.Login.selectedRecord || {}}
              //  QuotationTest={this.props.Login.QuotationTest}
                  onComboChange={this.onComboChange}
                  onInputOnChange={this.onInputOnChange}
          //      DiscountAmount={this.state.selectedRecord.DiscountAmount === undefined ? 0.00 :this.state.selectedRecord.DiscountAmount }
                  DiscountAmount={this.state.selectedRecord.DiscountAmount !== undefined ? this.state.selectedRecord.DiscountAmount:this.props.Login.QuotationGrossAmount[0].ndiscountamount}
        //        VatAmount={this.state.selectedRecord.VatAmount === undefined ? 0.00 : this.state.selectedRecord.VatAmount}
                  VatAmount={this.state.selectedRecord.VatAmount !== undefined ? this.state.selectedRecord.VatAmount:this.props.Login.QuotationGrossAmount[0].nvatamount}
                  QuotationGrossAmount={this.props.Login.QuotationGrossAmount === undefined ? 0.00 : this.props.Login.QuotationGrossAmount} 
                  TotalNetAmount={this.state.selectedRecord.TotalNetAmount !== undefined ? this.state.selectedRecord.TotalNetAmount:this.props.Login.QuotationGrossAmount[0].ntotalgrossamount}
                /> 
              : <AddQuotationPreview

                selectedQuotation ={this.props.Login.masterData.SelectedQuotation}
                userInfo={this.props.Login.userInfo}

              ></AddQuotationPreview>
           }
         />
        }
        {/* End of Modal Sideout for Test Creation */}
      </>
    );
  }

