        },
        operation:"delete",
     //  priceDataState:this.state.priceDataState
    }

    if (showEsign(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode, deleteParam.ncontrolCode)) {
        const updateInfo = {
            typeName: DEFAULT_RETURN,
