                                                                    methodUrl={"TechniqueTest"}
                                                                
                                                                    addRecord = {() => this.openModal(addId)}
                                                                    pageable={true}
                                                                    scrollable={'scrollable'}
                                                                
                                                                    isActionRequired={true}
