                            selectedRecord={this.state.selectedRecord || {}}
                        />
                        : this.props.Login.screenName === "IDS_TEST"  ? 
                            
                                <AddTechniqueTest
                                     selectedRecord={this.state.selectedRecord || {}}
                                     onComboChange={this.onComboChange}
                                     techniqueTestList={this.props.Login.techniqueTestList || []}/>
                                

                            : <AddType1Component
                                selectedRecord={this.state.selectedRecord || {}}
                                onInputOnChange={this.onInputOnChange}
