import React from 'react';
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';
import { Col, Row } from 'react-bootstrap';
import { injectIntl } from 'react-intl';
import { formCode, transactionStatus } from '../../components/Enumeration';

const SampleFilter = (props) => {
    return (
        <Row>
            <Col md={12}>
                <FormSelectSearch
                    formLabel={props.intl.formatMessage({ id: "IDS_SAMPLETYPE" })}
                    isSearchable={true}
                    name={"nsampletypecode"}
                    isDisabled={false}
                    placeholder={props.intl.formatMessage({ id: "IDS_PLEASESELECT" })}
                    isMandatory={false}
                    showOption={true}
                    options={props.sampleType || []}
                    optionId='nsampletypecode'
                    optionValue='ssampletypename'
                    value={props.tempFilterData["nsampletypecode"] ? props.tempFilterData["nsampletypecode"] : ""}
                    onChange={value => props.onFilterComboChange(value, "nsampletypecode", 1)}//, "getProductCategory", "sampletype"
                    sortOrder="ascending"
                >
                </FormSelectSearch>
            </Col>
            <Col md={12}>
                <FormSelectSearch
                    formLabel={props.intl.formatMessage({ id:props.tempFilterData["nsampletypecode"] ? props.tempFilterData["nsampletypecode"].item.nformcode===formCode.PRODUCTCATEGORY? "IDS_PRODUCTCATEGORY":
                    props.tempFilterData["nsampletypecode"].item.nformcode===formCode.INSTRUMENTCATEGORY?"IDS_INSTRUMENTCATEGORY":
                    props.tempFilterData["nsampletypecode"].item.nformcode===formCode.MATERIALCATEGORY?"IDS_MATERIALCATEGORY":"IDS_PRODUCTCATEGORY":"IDS_PRODUCTCATEGORY" })}
                    name={"nproductcatcode"}
                    placeholder={props.intl.formatMessage({ id: "IDS_PLEASESELECT" })}
                    optionId={"nproductcatcode"}
                    optionValue={"sproductcatname"}
                    options={props.productCategory || []}
                    showOption={true}
                    value={props.tempFilterData["nproductcatcode"] ? props.tempFilterData["nproductcatcode"] : ""}
                    isSearchable={true}
                    onChange={(event) => props.onFilterComboChange(event, "nproductcatcode", 2)}//, "getProduct", "productcategory"
                    sortOrder="ascending"
                >
                </FormSelectSearch>
            </Col>
            {
                props.tempFilterData["nproductcatcode"] &&
                props.tempFilterData["nproductcatcode"].item.ncategorybasedflow === transactionStatus.NO &&
                <Col md={12}>
                    <FormSelectSearch
                        formLabel={props.intl.formatMessage({ id:props.tempFilterData["nsampletypecode"] ?  props.tempFilterData["nsampletypecode"].item.nformcode===formCode.PRODUCTCATEGORY? "IDS_PRODUCT":
                        props.tempFilterData["nsampletypecode"].item.nformcode===formCode.INSTRUMENTCATEGORY?"IDS_INSTRUMENT":
                        props.tempFilterData["nsampletypecode"].item.nformcode===formCode.MATERIALCATEGORY?"IDS_MATERIAL":"IDS_PRODUCT":"IDS_PRODUCT" })}
                        name={"nproductcode"}
                        placeholder={props.intl.formatMessage({ id: "IDS_PLEASESELECT" })}
                        optionId={"nproductcode"}
                        optionValue={"sproductname"}
                        options={props.product || []}
                        showOption={true}
                        value={props.tempFilterData["nproductcode"] ? props.tempFilterData["nproductcode"] : ""}
                        isSearchable={true}
                        onChange={(event) => props.onFilterComboChange(event, "nproductcode", 4)}//, "getTreeVersionTemplate", "product"
                        sortOrder="ascending"
                    >
                    </FormSelectSearch>
                </Col>
            }
            <Col md={12}>
                <FormSelectSearch
                    formLabel={props.intl.formatMessage({ id: "IDS_TREETEMPLATEVERSION" })}
                    name={"ntreeversiontempcode"}
                    placeholder={props.intl.formatMessage({ id: "IDS_PLEASESELECT" })}
                    optionId={"ntreeversiontempcode"}
                    optionValue={"sversiondescription"}
                    options={props.treeVersionTemplate || []}
                    showOption={true}
                    value={props.tempFilterData["ntreeversiontempcode"] ? props.tempFilterData["ntreeversiontempcode"] : ""}
                    isSearchable={true}
                    onChange={(event) => props.onFilterComboChange(event, "ntreeversiontempcode", 4)}//, "getTreeVersionTemplate", "product"
                    sortOrder="ascending"
                >
                </FormSelectSearch>
            </Col>
        </Row>
    );
};

export default injectIntl(SampleFilter);
