            this.state.treeData[0],clickedItem) 
        if(ParentItem){
            ParentItem.items.map(item=>{
                item.expanded=false;
            }); 
        } 
        this.shrinkNodes(event.item);
      }
        event.item.expanded = !event.item.expanded;
 
        this.forceUpdate();
    };

