import { Grid, GridColumn } from '@progress/kendo-react-grid';
import { LocalizationProvider } from '@progress/kendo-react-intl';
import React from 'react'
import { injectIntl } from 'react-intl'
import 'rc-tabs/assets/index.css';
import { AtTabs } from '../../components/custom-tabs/custom-tabs.styles';
import CustomSwitch from '../../components/custom-switch/custom-switch.component';

import PerfectScrollbar from 'react-perfect-scrollbar';
import Tabs, { TabPane } from "rc-tabs";
import { AtTableWrap } from '../../components/data-grid/data-grid.styles';
import { formCode, SampleType } from '../../components/Enumeration';
import DataGrid from '../../components/data-grid/data-grid.component';

class ReleaseReportHistory extends React.Component {
    constructor(props) {
        super(props);
        const preliminaryHistoryDataState = {
            skip: 0,
            take: this.props.settings ? parseInt(this.props.settings[14]) : 5,
        };
        const releaseyHistoryDataState = {
            skip: 0,
            take: this.props.settings ? parseInt(this.props.settings[14]) : 5,
        };
        const regenerateHistoryDataState = {
            skip: 0,
            take: this.props.settings ? parseInt(this.props.settings[14]) : 5,
        };
    
        const screens = [];      

        screens.push({ eventKey: 'PreliminaryReportHistory', name: "IDS_PRELIMINARYHISTORY", formcode: formCode.RESULTENTRY },
                    { eventKey: 'ReleaseReportHistory', name: "IDS_RELEASEHISTORY", formcode: formCode.APPROVAL },
                    { eventKey: 'RegenerateReportHistory', name: "IDS_REGENERATEHISTORY", formcode: formCode.RELEASE }
                    );

        
           
        
        
        this.state = {
            screens, preliminaryHistoryDataState: preliminaryHistoryDataState, releaseyHistoryDataState: releaseyHistoryDataState,
            regenerateHistoryDataState:regenerateHistoryDataState,
            selectedScreen: { eventKey: 'PreliminaryReportHistory', name: "IDS_PRELIMINARYHISTORY", formcode: 43 }
        }
    }
    onTabChange = (tab) => {
        this.setState({
            selectedScreen: this.state.screens.find(screen => screen.eventKey === tab)
        })
    }
    preliminaryHistoryDataStateChange = (event) => {


        this.setState({ preliminaryHistoryDataState: event.dataState, dataResult: process(this.state.data, event.dataState) })
        // this.expandNextData(event.dataState);
    }
    releaseHistoryDataStateChange = (event) => {


        this.setState({ releaseyHistoryDataState: event.dataState, dataResult: process(this.state.data, event.dataState) })
        // this.expandNextData(event.dataState);
    }
    regenerateHistoryDataStateChange = (event) => {


        this.setState({ regenerateHistoryDataState: event.dataState, dataResult: process(this.state.data, event.dataState) })
        // this.expandNextData(event.dataState);
    }
    render() {
        //console.log("props:",this.props, this.state.screens, this.state.selectedScreen);
        const extractedColumnList = [];
        if (this.state && this.state.selectedScreen &&
            (this.state.selectedScreen.eventKey === "ReleaseReportHistory" || this.state.selectedScreen.eventKey === "RegenerateReportHistory")) {
            extractedColumnList.push(
                { "idsName": "IDS_REPORTREFERENCENO", "dataField": "sreportno", "width": "100px" },
                { "idsName": "IDS_VERSION", "dataField": "nversionno", "width": "75px" },
                { "idsName": "IDS_USER", "dataField": "susername", "width": "100px" },
                { "idsName": "IDS_USERROLE", "dataField": "suserrolename", "width": "100px" },
                { "idsName": "IDS_GENERATEDDATETIME", "dataField": "sgenerateddate", "width": "155px" }
            );
        }  
        else {
            extractedColumnList.push(
                { "idsName": "IDS_PRELIMINARYREFERENCENO", "dataField": "ncoaparentcode", "width": "100px" },
                { "idsName": "IDS_USER", "dataField": "susername", "width": "100px" },
                { "idsName": "IDS_USERROLE", "dataField": "suserrolename", "width": "100px" },
                { "idsName": "IDS_GENERATEDDATETIME", "dataField": "sgenerateddate", "width": "155px" }
            );
        }
       
        return (
            <>
            {
                <AtTabs>
                    <Tabs activeKey={this.state.selectedScreen.eventKey} moreIcon="..." onChange={this.onTabChange}>
                        {this.state.screens.map(screen =>
                            <TabPane name={screen.eventKey} tab={this.props.intl.formatMessage({ id: screen.name })} key={screen.eventKey}>
                                <PerfectScrollbar>
                                    {/* <ReactTooltip place="bottom" id="tooltip-grid-wrap" globalEventOff='click' /> */}
                                    <AtTableWrap className="at-list-table">
                                        <LocalizationProvider language="lang">
                                            <>
                                            <DataGrid
                        key="ncoaparentcode"
                        primaryKeyField="ncoaparentcode"
                        data={this.props.dataResult[this.state.selectedScreen.eventKey] || []}
                        dataResult={this.props.dataResult[this.state.selectedScreen.eventKey]}
                        dataState={this.state && this.state.selectedScreen &&
                            this.state.selectedScreen.eventKey === "PreliminaryReportHistory"?this.state.preliminaryHistoryDataState:
                            this.state && this.state.selectedScreen &&
                            this.state.selectedScreen.eventKey === "ReleaseReportHistory"?this.state.releaseyHistoryDataState:this.state.regenerateHistoryDataState}
                        //expandField="expanded"
                        isExportExcelRequired={false}
                        dataStateChange={this.state && this.state.selectedScreen &&
                            this.state.selectedScreen.eventKey === "PreliminaryReportHistory"?this.preliminaryHistoryDataStateChange:
                            this.state && this.state.selectedScreen &&
                            this.state.selectedScreen.eventKey === "ReleaseReportHistory"?this.releaseHistoryDataStateChange:this.regenerateHistoryDataStateChange}
                        extractedColumnList = {extractedColumnList}
                        controlMap={this.props.controlMap}
                        userRoleControlRights={this.props.userRoleControlRights}
                    // detailedFieldList={this.props.detailedFieldList}
                    // //editParam={editReportParam}
                    // selectedId={this.props.Login.selectedId}
                    // fetchRecord={this.props.fetchReportInfoReleaseById}
                        pageable={true}
                        // dataStateChange={this.dataStateChange}
                        scrollable={'scrollable'}
                        gridHeight={'630px'}
                        isActionRequired={this.state&&this.state.selectedScreen.eventKey==="RegenerateReportHistory"?false:true}
                        methodUrl={'ReleaseVersion'}
                        isDownload={this.state&&this.state.selectedScreen.eventKey==="RegenerateReportHistory"?false:true}
                        viewDownloadFile={this.props.viewDownloadFile}
                    // actionIcons={[{
                    //     title: this.props.intl.formatMessage({ id: "IDS_PREVIOUSRESULTVIEW" }),
                    //     controlname: "faEye",
                    //     objectName: "ExceptionLogs",
                    //     hidden: -1 === -1,
                    //    onClick: (viewSample) => this.props.viewSample(viewSample)
                    // }]}
                >
                </DataGrid>

                                            </>
                                        </LocalizationProvider>
                                    </AtTableWrap>
                                    {/* <ReactTooltip /> */}
                                </PerfectScrollbar>
                            </TabPane>
                        )}
                    </Tabs>
                </AtTabs>
            }
            </>
        );
    }
}
export default injectIntl(ReleaseReportHistory)
