import { Affix } from 'rsuite';
import ReactTooltip from 'react-tooltip';

class templatemaster extends Component {

    constructor(props) {
        super(props);
        this.state = {
            openModal: false,
            userRoleControlRights: [],
            templateTreeData: [],
            TMvalues: [{ TMvalue: "" }],
            selected: {},
            selectedValues: undefined,
