import Axios from "axios";
import { toast } from "react-toastify";
import { intl } from "../components/App";
import { constructOptionList, replaceUpdatedObject, sortData } from "../components/CommonScript";
import { reportCOAType, REPORTTYPE } from "../components/Enumeration";
import TrainingUpdateChildTab from "../pages/competencemanagement/trainingupdate/TrainingUpdateChildTab";
import rsapi from "../rsapi";
import { initRequest } from "./LoginAction";
import { DEFAULT_RETURN } from "./LoginTypes";

export function getReleasedSelectedSampleSubSampleTest(userInfo, Data1, inputData) {


    return function (dispatch) {
      if (inputData !== undefined &&inputData.npreregno!=="") {

        dispatch(initRequest(true));
        return rsapi.post("release/updateRelease",

            inputData
        )
            .then(response => {
                if (response.data.statusCodeValue!==undefined&&response.data.statusCodeValue !== 200) {
                    toast.warn(intl.formatMessage({
                        id: response.data
                    }));
                    dispatch({
                        type: DEFAULT_RETURN,
                        payload: {
                            loading: false
                        }
                    });


                }
                else {
         
                    if (response.data["PortalStatus"] && response.data["PortalStatus"].length > 0) {
                        dispatch(UpdateExternalOrderStatus(response.data["PortalStatus"],inputData));
                    }
                    let masterData = {

                        ...Data1, ...response.data,
    

                    };

                    console.log("ReleasedTest,", masterData);
                     sortData(masterData.ReleaseHistory,"",'sreportno');
                  //  dispatch(generateReport(inputData,masterData))

                    dispatch({
                        type: DEFAULT_RETURN, payload: {
                            masterData,
                            change: inputData.change,
                            loading: false,
                            openModal:false
                        }
                    });
                }
            })

            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                    console.log("ErrorRelease,", error);

                }
                else {
                    toast.warn(error.response.data);
                }
            })


        }
        else {
            toast.warn(intl.formatMessage({
                id: "IDS_SELECTANYONESAMPLE"
            }));
        }
    }
}
export function getReleasedRegistrationType(inputData) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post("release/getRegistrationType", inputData)
            .then(response => {
                let responseData = { ...response.data }
                responseData = sortData(responseData)
                dispatch({
                    type: DEFAULT_RETURN, payload: {
                        masterData: {
                            ...inputData.masterData,
                            ...responseData,
                            SampleTypeValue: inputData.SampleTypeValue
                        },
                        loading: false
                    }
                })
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                }
                else {
                    toast.warn(error.response.data);
                }
            })
    }
}
export function getReleasedRegistrationSubType(inputData) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post("release/getRegistrationSubType", inputData)
            .then(response => {
                let responseData = { ...response.data }
                responseData = sortData(responseData)
                dispatch({
                    type: DEFAULT_RETURN, payload: {
                        masterData: {
                            ...inputData.masterData,
                            ...responseData,
                            RegTypeValue: inputData.RegTypeValue
                        },
                        loading: false
                    }
                })
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                }
                else {
                    toast.warn(error.response.data);
                }
            })
    }
}

export function getReleasedFilterBasedTest(inputData) {
    return function (dispatch) {
        if (inputData.napprovalversioncode) {
            dispatch(initRequest(true));
            rsapi.post("release/getFilterBasedTest", inputData)
                .then(response => {


                    let responseData = { ...response.data }
                    responseData = sortData(responseData)
                    dispatch({
                        type: DEFAULT_RETURN, payload: {
                            masterData: {
                                ...inputData.masterData,
                                ...responseData,
                                RegSubTypeValue: inputData.RegSubTypeValue,
                                ndesigntemplatemappingcode: inputData.ndesigntemplatemappingcode,
                                DesignTemplateMappingValue: inputData.DesignTemplateMappingValue
                            },
                            loading: false
                        }
                    })
                    if (response.data.rtn) {
                        toast.warn(response.data.rtn);
                    }

                })
                .catch(error => {
                    dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                    if (error.response.status === 500) {
                        toast.error(error.message);
                    }
                    else {
                        toast.warn(error.response.data);
                    }
                })
        }
        else {
            toast.warn(intl.formatMessage({
                id: "IDS_PLSSELECTALLVALUESINFILTER"
            }));
        }
    }

}

export function getReleasedApprovalVersion(inputParam) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post("release/getApprovalVersion", inputParam.inputData)
            .then(response => {
                let responseData = { ...response.data }
                responseData = sortData(responseData)
                dispatch({
                    type: DEFAULT_RETURN, payload: {
                        masterData: {
                            ...inputParam.masterData,
                            ...responseData,
                            // fromDate: inputParam.inputData.dfrom,
                            // toDate: inputParam.inputData.dto,
                        },
                        loading: false,
                    }
                })
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                }
                else {
                    toast.warn(error.response.data);
                }
            })
    }
}
export function getReleasedSample(inputParam) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post("release/getReleaseSample", inputParam.inputData)
            .then(response => {
                let responseData = { ...response.data }
                // responseData = sortData(responseData)
                let masterData = {} 
                if (response.data.ReleasedSampleDetails && response.data.ReleasedSampleDetails.length !== 0 ||
                    response.data.ReleaseSample && response.data.ReleaseSample.length === 0) {
                    masterData = {
                        ...inputParam.masterData,
                        ...responseData,
                    }
            }
                else {
                    responseData.ReleaseHistory = []
                    responseData.selectedReleaseHistory= []
                    masterData = {
                        ...inputParam.masterData,
                        ...responseData,
                    }
                }
                if (inputParam.searchRef !== undefined && inputParam.searchRef.current !== null) {
                    inputParam.searchRef.current.value = "";
                    masterData['searchedData'] = undefined
                }
                if (inputParam.searchSampleRef !== undefined && inputParam.searchSampleRef.current !== null) {
                    inputParam.searchSampleRef.current.value = "";
                    masterData['searchedData'] = undefined
                }
                if (inputParam.searchSubSampleRef !== undefined && inputParam.searchSubSampleRef.current !== null) {
                    inputParam.searchSubSampleRef.current.value = "";
                    masterData['searchedSubSample'] = undefined
                }
                if (inputParam.searchTestRef !== undefined && inputParam.searchTestRef.current !== null) {
                    inputParam.searchTestRef.current.value = ""
                    // masterData['searchedTest'] = undefined
                    masterData['searchedTests'] = undefined

                }
                sortData(masterData);
                dispatch({
                    type: DEFAULT_RETURN, payload: {
                        masterData,
                        loading: false,
                        showFilter: false,
                        skip: 0,
                        take: inputParam.take,
                        testskip: 0,
                        testtake: inputParam.testtake,
                        resultDataState: { ...inputParam.resultDataState, sort: undefined, filter: undefined },
                        instrumentDataState: { ...inputParam.instrumentDataState, sort: undefined, filter: undefined },
                        materialDataState: { ...inputParam.materialDataState, sort: undefined, filter: undefined },
                        taskDataState: { ...inputParam.taskDataState, sort: undefined, filter: undefined },
                        documentDataState: { ...inputParam.documentDataState, sort: undefined, filter: undefined },
