      const Test = this.props.Login.Test;
      let SelectedTest = this.props.Login.SelectedTest;
      const index = Component.findIndex(x => x.slno === dataItem.slno);

      if (index > -1) {
        Component.splice(index, 1);
        subSampleDataGridList.splice(index, 1);
      }

