        const Layout = this.props.Login.masterData.SubSampleTemplate && this.props.Login.masterData.SubSampleTemplate.jsondata;

        if (SubSamplewithoutCombocomponent.length === 0 || data.length === 0) {
          if (Layout !== undefined) {
