                    //      specBasedComponent });

                    // this.setState({
                    //     selectedTestData: [], loadSpec: false, SelectedTest: [], Test: [], Component: [], selectedSpec
                    // })
                    const updateInfo = {
                        typeName: DEFAULT_RETURN,
                        data: {
                            selectedTestData: [],
                            selectComponent: [],
