                    selectedRecord[ProductCategory[0].label]['item']['jsondata']['ncategorybasedflow'];
                nproductcatcode = selectedRecord[ProductCategory[0].label] &&
                    selectedRecord[ProductCategory[0].label].value
                if (ncategorybasedflow !== 3) {
                    product = true
