    deleteComponent = (event) => {
        const dataItem = event;
        const Component = this.props.Login.Component;
        const subSampleDataGridList = this.props.Login.subSampleDataGridList;
        const Test = this.props.Login.Test;
        let SelectedTest = this.props.Login.SelectedTest;
        const index = Component.findIndex(x => x.slno === dataItem.slno);
        if (index > -1) {
            Component.splice(index, 1);
            subSampleDataGridList.splice(index, 1);
        }
        Test && Test[dataItem.slno] && delete Test[dataItem.slno]
        SelectedTest = Test && Object.keys(Test).length > 0 && Test[Component[0].slno];
        const updateInfo = {
            typeName: DEFAULT_RETURN,
            data: { Component, selectedComponent: Component[0], Test, SelectedTest }
        }
        this.props.updateStore(updateInfo);
    }

    deleteTest = (event) => {
        const dataItem = event;
        let SelectedTest = this.props.Login.SelectedTest;
        let selectedComponent = this.props.Login.selectedComponent;
        const Test = this.props.Login.Test;
        const index = SelectedTest.findIndex(x => x.ntestgrouptestcode === dataItem.ntestgrouptestcode);
        if (index > -1) {
            SelectedTest.splice(index, 1);
        }
        Test[selectedComponent.slno] = SelectedTest;

        const updateInfo = {
            typeName: DEFAULT_RETURN,
            data: { Test, SelectedTest }
        }
        this.props.updateStore(updateInfo);
    }

