          value: this.props.Login.masterData.SelectedInsCat.ninstrumentcatcode,
          item: this.props.Login.masterData.SelectedInsCat
        };
      }

      filterData = this.generateBreadCrumData();
      updateState = true;
    }

    if (this.props.Login.masterData.filterInstrumentCategory !== previousProps.Login.masterData.filterInstrumentCategory) {
      const insCategoryMap = constructOptionList(this.props.Login.masterData.filterInstrumentCategory || [], "ninstrumentcatcode", "sinstrumentcatname", "ntestcategorycode", "ascending", false);
      filterInstrumentCategory = insCategoryMap.get("OptionList");

      if (insCategoryMap.get("DefaultValue")) {
        nfilterInstrumentCategory = insCategoryMap.get("DefaultValue");
      } else if (filterInstrumentCategory && filterInstrumentCategory.length > 0) {
        nfilterInstrumentCategory = filterInstrumentCategory[0];
      }

      updateState = true;
    }

    if (updateState) {
      this.setState({
        selectedRecord,
        userRoleControlRights,
        controlMap,
        filterData,
        nfilterInstrumentCategory,
        filterInstrumentCategory
      });
    }
  }

  generateBreadCrumData() {
    const breadCrumbData = [];

    if (this.props.Login.masterData && this.props.Login.masterData.filterInstrumentCategory) {
      breadCrumbData.push({
        label: "IDS_INSTRUMENTCATEGORY",
        value: this.props.Login.masterData.SelectedInsCat ? this.props.Login.masterData.SelectedInsCat.sinstrumentcatname : "NA"
      });
    }

    return breadCrumbData;
  }

