});dispatch({type:DEFAULT_RETURN,payload:{openModal:true,operation:"update",screenName:"IDS_SPECFILE",editFiles:Object.values(editObject).length>0&&editObject.nattachmenttypecode===attachmentType.FTP?editObject:{},selectedRecord:selectedRecord,ncontrolCode:inputParam.ncontrolCode}});}).catch(function(error){if(error.response.status===409||error.response.status===417){toast.warn(error.response.data);}else{toast.error(error.message);}});}else{toast.warn(intl.formatMessage({id:"IDS_SPECIFICATIONSTATUSMUSTBEDRAFTCORRECTION"}));}};};export var addTestGroupCodedResult=function addTestGroupCodedResult(operation,paramdata,userInfo,ncontrolCode,optionalData){return function(dispatch){var testgroupspecification=optionalData.testgroupspecification;if(testgroupspecification.napprovalstatus===transactionStatus.DRAFT||testgroupspecification.napprovalstatus===transactionStatus.CORRECTION){dispatch(initRequest(true));var urlArray=[rsapi.post("grade/getGrade",{"userinfo":userInfo})];if(operation==="update"){urlArray.push(rsapi.post("testgroup/getActivePredefinedParameterById",{testgrouptestpredefinedparameter:paramdata,"userinfo":userInfo,testgroupspecification:testgroupspecification}));}Axios.all(urlArray).then(function(response){var selectedRecord={};var gradeMap=constructOptionList(response[0].data||[],"ngradecode","sdisplaystatus",false,false,true);var grade=gradeMap.get("OptionList");if(operation==="update"){var editCodedResult=response[1].data;selectedRecord={ntestgrouptestpredefcode:paramdata["ntestgrouptestpredefcode"],ntestgrouptestparametercode:paramdata["ntestgrouptestparametercode"],ngradecode:{"label":editCodedResult["sdisplaystatus"],"value":editCodedResult["ngradecode"]},spredefinedname:editCodedResult["spredefinedname"]};}else{selectedRecord={ngradecode:gradeMap.get("DefaultValue")// {
