      let conditionArrayString;

      if (selectedFieldRecord["filtercolumn"] && selectedFieldRecord['condition'] && (selectedFieldRecord['staticfiltervalue'] || selectedFieldRecord['filtervalue'])) {
        switch (selectedFieldRecord["filtercolumn"].type) {
          case 'static':
            {
              filterColumnName = " and " + selectedFieldRecord.filtercolumn.item.columnname;
              filterValue = {
                label: selectedFieldRecord.staticfiltervalue,
                value: selectedFieldRecord.staticfiltervalue
              };
